import React from 'react';
import { calculateInitialEMA, updateEMA } from '../utils/ema';

// Simple chunk helper for controlled concurrency
function chunk(arr, size) {
  const out = [];
  for (let i = 0; i < arr.length; i += size) out.push(arr.slice(i, i + size));
  return out;
}

async function fetchKlines5m(symbol, limit = 300) {
  const url = `https://fapi.binance.com/fapi/v1/klines?symbol=${symbol}&interval=5m&limit=${limit}`;
  const res = await fetch(url);
  if (!res.ok) throw new Error(`klines fetch failed ${res.status}`);
  const data = await res.json();
  return data;
}

function computeEmaSnapshot(closes) {
  if (!closes || closes.length < 200) return null;
  const closesNum = closes.map((c) => parseFloat(c));
  // EMA at penultimate close
  const ema26Prev = calculateInitialEMA(closesNum.slice(0, closesNum.length - 1), 26);
  const ema200Prev = calculateInitialEMA(closesNum.slice(0, closesNum.length - 1), 200);
  // EMA at last close
  const last = closesNum[closesNum.length - 1];
  const ema26Last = updateEMA(ema26Prev, last, 26);
  const ema200Last = updateEMA(ema200Prev, last, 200);
  const statePrev = ema26Prev > ema200Prev ? 'bull' : 'bear';
  const stateNow = ema26Last > ema200Last ? 'bull' : 'bear';
  const crossedNow = statePrev !== stateNow;
  return { ema26Prev, ema200Prev, ema26Last, ema200Last, statePrev, stateNow, crossedNow, last };
}

export default function ScannerPage({ availableSymbols = [], defaultLimit = 50, showDebug = false }) {
  const [maxSymbols, setMaxSymbols] = React.useState(defaultLimit);
  const [onlyCross, setOnlyCross] = React.useState(false);
  const [stateFilter, setStateFilter] = React.useState('any'); // any | bull | bear
  const [results, setResults] = React.useState([]);
  const [scanning, setScanning] = React.useState(false);
  const [error, setError] = React.useState(null);

  const symbolsUsdt = React.useMemo(() => {
    const syms = (availableSymbols || []).filter((s) => /USDT$/i.test(s));
    // naive filter to avoid leveraged tokens
    return syms.filter((s) => !/(DOWN|UP|BULL|BEAR)/i.test(s));
  }, [availableSymbols]);

  async function scanNow() {
    setError(null);
    setScanning(true);
    setResults([]);
    try {
      const list = symbolsUsdt.slice(0, Math.max(1, Number(maxSymbols) || defaultLimit));
      const batches = chunk(list, 8); // concurrency of 8
      const out = [];
      for (const batch of batches) {
        const batchPromises = batch.map(async (sym) => {
          try {
            const data = await fetchKlines5m(sym, 300);
            if (!Array.isArray(data) || data.length < 200) return null;
            const closes = data.map((k) => k[4]);
            const closeTime = data[data.length - 1][6];
            const snap = computeEmaSnapshot(closes);
            if (!snap) return null;
            const row = {
              symbol: sym,
              lastPrice: snap.last,
              ema26: snap.ema26Last,
              ema200: snap.ema200Last,
              state: snap.stateNow,
              crossedNow: snap.crossedNow,
              time: new Date(closeTime).toLocaleString(),
            };
            return row;
          } catch (e) {
            if (showDebug) console.debug('[Scanner] failed', sym, e);
            return null;
          }
        });
        const batchRes = await Promise.all(batchPromises);
        for (const r of batchRes) if (r) out.push(r);
      }
      let filtered = out;
      if (onlyCross) filtered = filtered.filter((r) => r.crossedNow);
      if (stateFilter === 'bull') filtered = filtered.filter((r) => r.state === 'bull');
      if (stateFilter === 'bear') filtered = filtered.filter((r) => r.state === 'bear');
      // sort: crossed first, then by absolute ema distance desc
      filtered.sort((a, b) => {
        const scoreA = (a.crossedNow ? 1 : 0) * 1e6 + Math.abs(a.ema26 - a.ema200);
        const scoreB = (b.crossedNow ? 1 : 0) * 1e6 + Math.abs(b.ema26 - b.ema200);
        return scoreB - scoreA;
      });
      setResults(filtered);
    } catch (e) {
      setError(String(e));
    } finally {
      setScanning(false);
    }
  }

  return (
    <div>
      <div style={{ display: 'flex', gap: 12, alignItems: 'center', marginBottom: 12 }}>
        <button className="primary" onClick={scanNow} disabled={scanning}>
          {scanning ? 'Scanning…' : 'Scan Now'}
        </button>
        <label>
          Max symbols:&nbsp;
          <input type="number" min={1} max={500} value={maxSymbols} onChange={(e) => setMaxSymbols(e.target.value)} style={{ width: 80 }} />
        </label>
        <label style={{ display: 'flex', alignItems: 'center', gap: 6 }}>
          <input type="checkbox" checked={onlyCross} onChange={(e) => setOnlyCross(e.target.checked)} />
          Only show new crosses (last closed)
        </label>
        <label>
          State:&nbsp;
          <select value={stateFilter} onChange={(e) => setStateFilter(e.target.value)}>
            <option value="any">Any</option>
            <option value="bull">Bullish (EMA26 &gt; EMA200)</option>
            <option value="bear">Bearish (EMA26 &lt; EMA200)</option>
          </select>
        </label>
        <div style={{ marginLeft: 'auto', fontSize: '0.8rem', color: '#666' }}>
          Scanning universe: {symbolsUsdt.length} USDT pairs
        </div>
      </div>
      {error && <div style={{ color: '#a00', marginBottom: 8 }}>Error: {error}</div>}
      <div style={{ overflow: 'auto' }}>
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ textAlign: 'left', borderBottom: '1px solid #ddd' }}>
              <th style={{ padding: '8px 6px' }}>Symbol</th>
              <th style={{ padding: '8px 6px' }}>Last Price</th>
              <th style={{ padding: '8px 6px' }}>EMA26</th>
              <th style={{ padding: '8px 6px' }}>EMA200</th>
              <th style={{ padding: '8px 6px' }}>State</th>
              <th style={{ padding: '8px 6px' }}>Crossed</th>
              <th style={{ padding: '8px 6px' }}>Time</th>
            </tr>
          </thead>
          <tbody>
            {results.length === 0 && (
              <tr>
                <td colSpan={7} style={{ padding: '12px 6px', color: '#666' }}>
                  {scanning ? 'Scanning…' : 'No results yet. Click "Scan Now".'}
                </td>
              </tr>
            )}
            {results.map((r) => (
              <tr key={r.symbol} style={{ borderBottom: '1px solid #f0f0f0' }}>
                <td style={{ padding: '8px 6px', fontWeight: 600 }}>{r.symbol}</td>
                <td style={{ padding: '8px 6px' }}>{r.lastPrice}</td>
                <td style={{ padding: '8px 6px' }}>{r.ema26?.toFixed(6)}</td>
                <td style={{ padding: '8px 6px' }}>{r.ema200?.toFixed(6)}</td>
                <td style={{ padding: '8px 6px', color: r.state === 'bull' ? '#0a0' : '#a00' }}>{r.state}</td>
                <td style={{ padding: '8px 6px' }}>{r.crossedNow ? 'Yes' : 'No'}</td>
                <td style={{ padding: '8px 6px' }}>{r.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
