import logo from './logo.svg';
import './App.css';
import React, { useEffect, useRef, useState } from 'react';
import useEmaCross from './hooks/useEmaCross';

function beep() {
  try {
    const AudioCtx = window.AudioContext || window.webkitAudioContext;
    const ctx = new AudioCtx();
    const o = ctx.createOscillator();
    const g = ctx.createGain();
    o.type = 'sine';
    o.frequency.value = 880;
    o.connect(g);
    g.connect(ctx.destination);
    o.start();
    g.gain.setValueAtTime(0.0001, ctx.currentTime);
    g.gain.exponentialRampToValueAtTime(0.1, ctx.currentTime + 0.01);
    g.gain.exponentialRampToValueAtTime(0.0001, ctx.currentTime + 0.6);
    setTimeout(() => {
      o.stop();
      try { ctx.close(); } catch (e) {}
    }, 700);
  } catch (e) {
    // ignore
  }
}

function App() {
  const [symbol, setSymbol] = useState('BTCUSDT');
  const { ema26, ema200, lastPrice, cross, connected, status, connect, disconnect } = useEmaCross({ symbol });
  const [events, setEvents] = useState([]);
  const lastNotified = useRef(null);

  // Ask for notification permission on first user interaction
  useEffect(() => {
    const handle = () => {
      if ('Notification' in window && Notification.permission === 'default') {
        Notification.requestPermission().then(() => {});
      }
      window.removeEventListener('click', handle);
    };
    window.addEventListener('click', handle);
    return () => window.removeEventListener('click', handle);
  }, []);

  // react to cross changes and notify (skip initial)
  useEffect(() => {
    if (!cross) return;
    if (lastNotified.current == null) {
      // seed without notifying
      lastNotified.current = cross;
      return;
    }

    if (lastNotified.current !== cross) {
      const type = cross === 'bull' ? 'Bullish EMA26 > EMA200' : 'Bearish EMA26 < EMA200';
      const body = `${symbol} ${type} @ ${lastPrice ?? 'N/A'}`;

      // browser notification
      if ('Notification' in window && Notification.permission === 'granted') {
        new Notification(type, { body });
      }

      // beep and log
      beep();
      setEvents((s) => [{ time: new Date().toLocaleString(), type: cross, price: lastPrice }, ...s].slice(0, 20));
      lastNotified.current = cross;
    }
  }, [cross, lastPrice, symbol]);

  return (
    <div className="App" style={{ padding: 20 }}>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" style={{ width: 64, height: 64 }} />
        <h2>Binance EMA Cross Alert (5m, EMA26 / EMA200)</h2>
      </header>

      <div style={{ marginTop: 12 }}>
        <label>
          Symbol:{' '}
          <input value={symbol} onChange={(e) => setSymbol(e.target.value.toUpperCase())} />
        </label>
        <button onClick={() => connect()} style={{ marginLeft: 8 }}>Start</button>
        <button onClick={() => disconnect()} style={{ marginLeft: 8 }}>Stop</button>
      </div>

      <div style={{ marginTop: 12 }}>
        <strong>Status:</strong> {status} {connected ? '(connected)' : '(disconnected)'}
      </div>

      <div style={{ marginTop: 12 }}>
        <div><strong>Last price:</strong> {lastPrice ?? '—'}</div>
        <div><strong>EMA26:</strong> {ema26 ? ema26.toFixed(4) : '—'}</div>
        <div><strong>EMA200:</strong> {ema200 ? ema200.toFixed(4) : '—'}</div>
        <div><strong>Cross:</strong> {cross ?? '—'}</div>
      </div>

      <div style={{ marginTop: 16 }}>
        <h4>Alerts</h4>
        {events.length === 0 ? <div>No alerts yet</div> : (
          <ul>
            {events.map((ev, i) => (
              <li key={i}>{ev.time} — {ev.type.toUpperCase()} @ {ev.price}</li>
            ))}
          </ul>
        )}
      </div>

      <div style={{ fontSize: 12, marginTop: 16, color: '#666' }}>
        Notes: This app uses Binance public REST + websocket. Make sure your network allows websocket connections to Binance.
      </div>
    </div>
  );
}

export default App;
