import React, { useState } from 'react';

export default function Alerts({ events = [], removeAlertByTs, onMonitor, monitorMinutes = 5, monitorEma1 = 26, monitorEma2 = 200 }) {
  // Only show primary alert events (bull/bear). Hide telegram_send / telegram status events
  const visible = (events || []).filter((ev) => {
    try {
      if (!ev || !ev.type) return false;
      const t = (ev.type || '').toString();
      // hide auxiliary telegram events
      if (t.startsWith('telegram')) return false;
      // show only bull/bear or similar alert types
      return t === 'bull' || t === 'bear' || t === 'alert';
    } catch (e) {
      return false;
    }
  });

  // local state for the inline monitor controls (initialize from props)
  const [localSymbol, setLocalSymbol] = useState('');
  const [localMinutes, setLocalMinutes] = useState(monitorMinutes);
  const [localEma1, setLocalEma1] = useState(monitorEma1);
  const [localEma2, setLocalEma2] = useState(monitorEma2);

  const handleMonitor = () => {
    if (!onMonitor) return;
    const s = (localSymbol || '').toString().replace(/[^A-Za-z0-9]/g, '').toUpperCase();
    if (!s) return;
    onMonitor({ symbol: s, minutes: Number(localMinutes) || monitorMinutes, ema1: Number(localEma1) || monitorEma1, ema2: Number(localEma2) || monitorEma2 });
  };

  return (
    <div className="alerts">
      <div className="alerts-title">Alerts</div>

      <div className="controls" style={{display: 'flex', gap: 6, alignItems: 'center', marginBottom: 8, flexWrap: 'nowrap'}}>
        <label className="controls-label" style={{margin: 0}}>
          <span className="label-text">Symbol</span>
          <input type="text" value={localSymbol} onChange={(e) => setLocalSymbol(e.target.value.toUpperCase())} />
        </label>
        <label className="control-inline-label" style={{margin: 0}}>
          <span className="label-text">Mins</span>
          <input type="number" value={localMinutes} onChange={(e) => setLocalMinutes(Number(e.target.value || 0))} />
        </label>
        <label className="control-inline-label" style={{margin: 0}}>
          <span className="label-text">EMA1</span>
          <input type="number" value={localEma1} onChange={(e) => setLocalEma1(Number(e.target.value || 0))} />
        </label>
        <label className="control-inline-label" style={{margin: 0}}>
          <span className="label-text">EMA2</span>
          <input type="number" value={localEma2} onChange={(e) => setLocalEma2(Number(e.target.value || 0))} />
        </label>
        <button onClick={handleMonitor} disabled={!localSymbol || !onMonitor}>Monitor</button>
      </div>

      <ul className="alerts-list">
        {visible.map((ev, i) => (
          <li key={ev?.ts ?? i} className="alert-item">
            <div className="alert-left">
              <span className={`alert-indicator ${ev.type === 'bull' ? 'bull' : 'bear'}`} />
              <div className="alert-symbol">{ev.symbol || ''}</div>
            </div>

            <div className="alert-body">
              <div className="alert-info">
                <div className="alert-type-short">{ev.type === 'bull' ? 'Bull' : 'Bear'}</div>
                <div className="alert-time">{ev.time}</div>
              </div>
              <div className="alert-right">
                <div className="alert-price">{typeof ev.price === 'number' ? ev.price : ev.price}</div>
                <div style={{fontSize:'0.7rem', color:'#666', marginTop:4}}>{ev.source || ''}</div>
                <button className="delete-btn" onClick={() => removeAlertByTs(ev.ts)} aria-label="삭제" title="삭제">✕</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
