import React, { useEffect, useMemo, useState, useRef } from 'react';
import scannerManager from '../lib/scannerManager';

export default function ScannerPage({ availableSymbols, fetchExchangeInfo, monitorMinutes, setMonitorMinutes, monitorEma1, setMonitorEma1, monitorEma2, setMonitorEma2 }) {
  const symbols = useMemo(() => (Array.isArray(availableSymbols) ? availableSymbols.slice() : []), [availableSymbols]);
  const [state, setState] = useState(scannerManager.getState());

  useEffect(() => {
    // provide symbol list provider to manager
    scannerManager.setGetSymbols(() => symbols || []);
  }, [symbols]);

  useEffect(() => {
    // ensure we have exchange info loaded
    if ((!symbols || symbols.length === 0) && typeof fetchExchangeInfo === 'function') {
      try { fetchExchangeInfo(); } catch (e) {}
    }
  }, [symbols, fetchExchangeInfo]);

  useEffect(() => {
    // subscribe to manager updates
    const off = scannerManager.onUpdate((s) => setState(s));
    // initialize state
    setState(scannerManager.getState());
    return () => off();
  }, []);

  function startGolden() { try { scannerManager.start('golden', { interval: monitorMinutes, emaShort: monitorEma1, emaLong: monitorEma2 }); } catch (e) {} }
  function startDead() { try { scannerManager.start('dead', { interval: monitorMinutes, emaShort: monitorEma1, emaLong: monitorEma2 }); } catch (e) {} }
  function stopScan() { try { scannerManager.stop(); } catch (e) {} }

  const running = !!state.running;
  const currentSymbol = state.currentSymbol;
  const progress = state.progress || { done: 0, total: 0 };
  const results = useMemo(() => (Array.isArray(state.results) ? state.results.slice() : []), [state.results]);

  // modal state for newly detected matches
  const [modalVisible, setModalVisible] = useState(false);
  const [modalData, setModalData] = useState(null);
  const shownResultsRef = useRef(new Set());

  // small beep helper (reuse pattern from App.beep)
  function beep() {
    try {
      const AudioCtx = window.AudioContext || window.webkitAudioContext;
      const ctx = new AudioCtx();
      const o = ctx.createOscillator();
      const g = ctx.createGain();
      o.type = 'sine';
      o.frequency.value = 880;
      o.connect(g);
      g.connect(ctx.destination);
      o.start();
      g.gain.setValueAtTime(0.0001, ctx.currentTime);
      g.gain.exponentialRampToValueAtTime(0.1, ctx.currentTime + 0.01);
      g.gain.exponentialRampToValueAtTime(0.0001, ctx.currentTime + 0.6);
      setTimeout(() => {
        o.stop();
        try { ctx.close(); } catch (e) {}
      }, 700);
    } catch (e) {
      // ignore
    }
  }

  // show modal + beep when a new match is detected
  useEffect(() => {
    try {
      if (!results || results.length === 0) return;
      const top = results[0];
      if (!top || !top.symbol) return;
      const id = `${top.symbol}::${top.time}::${top.emaShort}::${top.emaLong}`;
      if (shownResultsRef.current.has(id)) return;
      // mark shown and present modal
      shownResultsRef.current.add(id);
      try { beep(); } catch (e) {}
      setModalData(top);
      setModalVisible(true);
    } catch (e) {}
  }, [results]);

  return (
    <div className="alerts" style={{ padding: 8 }}>
      {modalVisible && modalData && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.45)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 9999 }}>
          <div style={{ background: '#fff', padding: 20, borderRadius: 8, width: 420, maxWidth: '90%', boxShadow: '0 8px 30px rgba(0,0,0,0.25)' }}>
            <div style={{ fontSize: '1.1rem', fontWeight: 700, marginBottom: 8 }}>Match detected: {String(modalData.symbol || '')}</div>
            <div style={{ fontSize: '0.95rem', color: '#333', marginBottom: 12 }}>
              Interval: {String(modalData.interval || '')} Â· EMA {String(modalData.emaShort || '')} / EMA {String(modalData.emaLong || '')}
            </div>
            <div style={{ fontSize: '0.85rem', color: '#555', marginBottom: 16 }}>{String(modalData.time || '')}</div>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
              <button onClick={() => { try { setModalVisible(false); } catch (e) {} }} style={{ padding: '6px 10px', borderRadius: 6, border: 'none', background: '#6b7280', color: '#fff' }}>Close</button>
            </div>
          </div>
        </div>
      )}
      <div className="alerts-title">Binance EMA Cross Scanner</div>
  <div style={{ border: '1px solid #e0e0e0', padding: 8, borderRadius: 8, background: '#fbfbfb', margin: 8, display: 'flex', gap: 12, alignItems: 'center', flexWrap: 'nowrap', overflowX: 'auto' }}>
        <div style={{ display: 'flex', gap: 8, alignItems: 'center', flexWrap: 'wrap', flex: 1, minWidth: 220 }}>
          <label className="control-inline-label">
            <span className="label-text">Mins</span>
            <input type="number" min="1" value={monitorMinutes} onChange={(e) => setMonitorMinutes(Number(e.target.value || 0))} />
          </label>
          <label className="control-inline-label">
            <span className="label-text">EMA1</span>
            <input type="number" min="1" value={monitorEma1} onChange={(e) => setMonitorEma1(Number(e.target.value || 0))} />
          </label>
          <label className="control-inline-label">
            <span className="label-text">EMA2</span>
            <input type="number" min="1" value={monitorEma2} onChange={(e) => setMonitorEma2(Number(e.target.value || 0))} />
          </label>
        </div>

        <div className="scanner-controls" style={{ display: 'flex', flexDirection: 'row', gap: 8, alignItems: 'center', justifyContent: 'flex-end', whiteSpace: 'nowrap' }}>
          <button onClick={startGolden} disabled={running} className="scanner-btn green" style={{ padding: '4px 8px', fontSize: '0.8rem', minWidth: 72 }}>Golden</button>
          <button onClick={startDead} disabled={running} className="scanner-btn red" style={{ padding: '4px 8px', fontSize: '0.8rem', minWidth: 72 }}>Dead</button>
          <button onClick={stopScan} disabled={!running} className="scanner-btn stop" style={{ padding: '4px 8px', fontSize: '0.8rem', minWidth: 72 }}>Stop</button>
        </div>
      </div>

      {/* progress bar: shows scanning progress below the buttons */}
      <div style={{ width: '100%', margin: '8px 0' }} aria-hidden={!running}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 6 }}>
          <div style={{ fontSize: '0.85rem', color: '#444' }}>{running ? `Scanning: ${currentSymbol || '...'}` : 'Idle'}</div>
          <div style={{ fontSize: '0.85rem', color: '#666' }}>{progress.done}/{progress.total || (symbols || []).filter(s => /USDT$/i.test(s)).length}</div>
        </div>
        <div style={{ background: '#eee', height: 10, borderRadius: 6, overflow: 'hidden' }}>
          {(() => {
            const total = (progress && progress.total) ? progress.total : ((symbols || []).filter(s => /USDT$/i.test(s)).length || 0);
            const done = (progress && progress.done) ? progress.done : 0;
            const pct = total > 0 ? Math.min(100, Math.round((done / total) * 100)) : 0;
            return (
              <div role="progressbar" aria-valuemin={0} aria-valuemax={100} aria-valuenow={pct} style={{ width: `${pct}%`, height: '100%', background: running ? '#4caf50' : '#ccc', transition: 'width 300ms ease' }} />
            );
          })()}
        </div>
      </div>

      <ul className="alerts-list">
        {results.length === 0 ? (
          <li className="alert-item" style={{ color: '#666' }}>No matches yet.</li>
        ) : (
          results.map((r, idx) => (
            <li key={`${r.symbol}-${idx}`} className="alert-item">
              <div className="alert-left">
                <span className={`alert-indicator bull`} />
                <div className="alert-symbol">{r.symbol}</div>
              </div>
              <div className="alert-body">
                <div className="alert-info">
                  <div className="alert-type-short">Match</div>
                  <div className="alert-time">{r.time}</div>
                </div>
                <div className="alert-right">
                  <div className="alert-price">{Number(r.last26).toFixed(6)} / {Number(r.last200).toFixed(6)}</div>
                </div>
              </div>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}
