import { useEffect, useRef, useState, useCallback } from 'react';
import { calculateInitialEMA, updateEMA } from '../utils/ema';

// Hook options: { symbol }
export default function useEmaCross({ symbol = 'BTCUSDT', autoConnect = true } = {}) {
  const [ema26, setEma26] = useState(null);
  const [ema200, setEma200] = useState(null);
  const [lastPrice, setLastPrice] = useState(null);
  const [cross, setCross] = useState(null); // 'bull' | 'bear' | null
  const [connected, setConnected] = useState(false);
  const [status, setStatus] = useState('idle');
  const [lastCandleClosed, setLastCandleClosed] = useState(false);

  const wsRef = useRef(null);
  const prevCrossRef = useRef(null);
  const ema26Ref = useRef(null);
  const ema200Ref = useRef(null);
  const reconnectAttemptsRef = useRef(0);
  const reconnectTimerRef = useRef(null);

  const fetchAndInit = useCallback(async () => {
    try {
      setStatus('fetching historical klines');
      const url = `https://api.binance.com/api/v3/klines?symbol=${symbol}&interval=5m&limit=500`;
      const res = await fetch(url);
      if (!res.ok) throw new Error(`Failed to fetch klines: ${res.status}`);
      const data = await res.json();
      // kline array: [ openTime, open, high, low, close, ... ]
      const closes = data.map((k) => parseFloat(k[4]));

      if (closes.length < 200) throw new Error('Not enough historical candles to initialize EMA200');

      // Use the full close history to compute EMA values
      const initEma26 = calculateInitialEMA(closes.slice(-300), 26);
      const initEma200 = calculateInitialEMA(closes.slice(-500), 200);

      ema26Ref.current = initEma26;
      ema200Ref.current = initEma200;
      setEma26(initEma26);
      setEma200(initEma200);
      setLastPrice(closes[closes.length - 1]);
      setStatus('initialized');
      // set initial cross
      const initialCross = initEma26 > initEma200 ? 'bull' : 'bear';
      prevCrossRef.current = initialCross;
      setCross(initialCross);
    } catch (err) {
      setStatus(`init error: ${err.message}`);
      console.error(err);
    }
  }, [symbol]);

  const connect = useCallback(() => {
    if (wsRef.current) return;
  setStatus('connecting websocket');
  // Use combined stream: kline_5m + aggTrade for higher-frequency trade updates
  const klineStream = `${symbol.toLowerCase()}@kline_5m`;
  const tradeStream = `${symbol.toLowerCase()}@aggTrade`;
  const streams = `${klineStream}/${tradeStream}`;
  const url = `wss://stream.binance.com:9443/stream?streams=${streams}`;
  const ws = new WebSocket(url);
    wsRef.current = ws;

    ws.onopen = () => {
      // reset backoff attempts on successful open
      reconnectAttemptsRef.current = 0;
      if (reconnectTimerRef.current) {
        clearTimeout(reconnectTimerRef.current);
        reconnectTimerRef.current = null;
      }
      setConnected(true);
      setStatus('connected');
    };

    ws.onmessage = (ev) => {
      try {
        const payloadWrapper = JSON.parse(ev.data);
        // combined stream returns { stream, data }
        const payload = payloadWrapper.data || payloadWrapper;

        // aggTrade messages have event type 'aggTrade' and price in p
        if (payload.e === 'aggTrade') {
          const price = parseFloat(payload.p);
          setLastPrice(price);

          if (ema26Ref.current == null || ema200Ref.current == null) return;

          // update EMA using trade price to provide higher-frequency preview
          const newEma26 = updateEMA(ema26Ref.current, price, 26);
          const newEma200 = updateEMA(ema200Ref.current, price, 200);
          ema26Ref.current = newEma26;
          ema200Ref.current = newEma200;
          setEma26(newEma26);
          setEma200(newEma200);

          const newCross = newEma26 > newEma200 ? 'bull' : 'bear';
          if (prevCrossRef.current !== newCross) {
            prevCrossRef.current = newCross;
            setCross(newCross);
          }
        }

        // kline messages contain a 'k' object
        if (payload.k) {
          const k = payload.k;
          const close = parseFloat(k.c);
          setLastPrice(close);
          setLastCandleClosed(Boolean(k.x));

          if (ema26Ref.current == null || ema200Ref.current == null) return;

          // update EMA using kline close (partial or closed)
          const newEma26 = updateEMA(ema26Ref.current, close, 26);
          const newEma200 = updateEMA(ema200Ref.current, close, 200);
          ema26Ref.current = newEma26;
          ema200Ref.current = newEma200;
          setEma26(newEma26);
          setEma200(newEma200);

          const newCross = newEma26 > newEma200 ? 'bull' : 'bear';
          if (prevCrossRef.current !== newCross) {
            prevCrossRef.current = newCross;
            setCross(newCross);
          }
        }
      } catch (err) {
        console.error('ws message parse error', err);
      }
    };

    ws.onerror = (e) => {
      console.error('ws error', e);
      setStatus('websocket error');
      // close to trigger backoff reconnect
      try { ws.close(); } catch (err) {}
    };

    ws.onclose = () => {
      setConnected(false);
      setStatus('websocket closed');
      wsRef.current = null;
      // exponential backoff reconnect with jitter
      const attempt = reconnectAttemptsRef.current || 0;
      const base = 1000; // 1s
      const delay = Math.min(30000, base * Math.pow(2, attempt));
      const jitter = Math.floor(Math.random() * 1000);
      reconnectAttemptsRef.current = attempt + 1;
      reconnectTimerRef.current = setTimeout(() => {
        reconnectTimerRef.current = null;
        if (!wsRef.current) connect();
      }, delay + jitter);
    };
  }, [symbol]);

  const disconnect = useCallback(() => {
    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }
    setConnected(false);
    setStatus('disconnected');
  }, []);

  useEffect(() => {
    // initialize on mount (or when symbol changes)
    fetchAndInit();
    // cleanup on unmount
    return () => {
      if (wsRef.current) wsRef.current.close();
    };
  }, [fetchAndInit]);

  // auto connect after initialization if requested
  useEffect(() => {
    if (status === 'initialized' && autoConnect) {
      // call connect once after initialization
      connect();
    }
    // only run when status or autoConnect changes
  }, [status, autoConnect, connect]);

  return {
    ema26,
    ema200,
    lastPrice,
    lastCandleClosed,
    cross,
    connected,
    status,
    connect,
    disconnect,
  };
}
