import React, { useRef, useState, useEffect } from 'react';
import PropTypes from 'prop-types';

export default function Controls(props) {
  const {
    symbol,
    setSymbol,
    symbolValid,
    setSymbolValid,
    suggestions,
    setSuggestions,
    availableSymbols,
    validateSymbolOnce,
    connect,
    disconnect,
  status,
    // monitoring controls
    monitorMinutes,
    setMonitorMinutes,
    monitorEma1,
    setMonitorEma1,
    monitorEma2,
    setMonitorEma2,
  } = props;

  // local editable strings to allow clearing input and typing single digits
  const [localMinutes, setLocalMinutes] = useState(String(monitorMinutes || ''));
  const [localEma1, setLocalEma1] = useState(String(monitorEma1 || ''));
  const [localEma2, setLocalEma2] = useState(String(monitorEma2 || ''));

  useEffect(() => { setLocalMinutes(String(monitorMinutes || '')); }, [monitorMinutes]);
  useEffect(() => { setLocalEma1(String(monitorEma1 || '')); }, [monitorEma1]);
  useEffect(() => { setLocalEma2(String(monitorEma2 || '')); }, [monitorEma2]);

  function commitMinutes() {
    const v = String(localMinutes || '').replace(/[^0-9]/g, '');
    const n = v === '' ? null : parseInt(v, 10);
    if (n && n > 0) setMonitorMinutes(n);
  }
  function commitEma1() {
    const v = String(localEma1 || '').replace(/[^0-9]/g, '');
    const n = v === '' ? null : parseInt(v, 10);
    if (n && n > 0) setMonitorEma1(n);
  }
  function commitEma2() {
    const v = String(localEma2 || '').replace(/[^0-9]/g, '');
    const n = v === '' ? null : parseInt(v, 10);
    if (n && n > 0) setMonitorEma2(n);
  }

  const symbolValidateTimer = useRef(null);
  // reference suggestions to avoid lint 'assigned but not used' when parent passes it
  void suggestions;

  return (
    <div className="controls">
      <label className="controls-label">
        <span className="label-text">Symbol</span>
        <input type="text" value={symbol} onChange={(e) => {
          const v = e.target.value.toUpperCase();
          setSymbol(v);
          setSymbolValid(null);
          if (symbolValidateTimer.current) clearTimeout(symbolValidateTimer.current);
          symbolValidateTimer.current = setTimeout(() => {
            try {
              const q = v.replace(/[^A-Za-z0-9]/g, '').toUpperCase();
              const pool = availableSymbols || [];
              if (pool.length > 0) {
                const normQ = q;
                const exact = pool.some(sy => (sy || '').replace(/[^A-Za-z0-9]/g, '').toUpperCase() === normQ) || pool.includes(q);
                if (exact) {
                  setSymbolValid(true);
                  setSuggestions([]);
                  return;
                }
                const found = pool.filter(s => s.includes(q) || s.startsWith(q) || q.startsWith(s)).slice(0, 8);
                setSuggestions(found);
                setSymbolValid(found.length > 0 ? null : false);
                return;
              }

              (async () => {
                try {
                  const url = `https://api.binance.com/api/v3/exchangeInfo?symbol=${q}`;
                  const res = await fetch(url);
                  if (!res.ok) { setSymbolValid(false);return; }
                  const data = await res.json();
                  if (data && (data.symbols || data.symbol)) setSymbolValid(true);
                  else setSymbolValid(false);
                } catch (err) { setSymbolValid(false); }
              })();
            } catch (err) { setSymbolValid(false); setSuggestions([]); }
          }, 600);
        }} />
      </label>

      <label className="control-inline-label" style={{marginLeft:8}}>
        <span className="label-text">Mins</span>
        <input type="text" inputMode="numeric" pattern="\\d*" value={localMinutes} onChange={(e) => setLocalMinutes(e.target.value)} onBlur={commitMinutes} placeholder="e.g. 1" />
      </label>

      <label className="control-inline-label">
        <span className="label-text">EMA1</span>
        <input type="text" inputMode="numeric" pattern="\\d*" value={localEma1} onChange={(e) => setLocalEma1(e.target.value)} onBlur={commitEma1} placeholder="e.g. 9" />
      </label>

      <label className="control-inline-label">
        <span className="label-text">EMA2</span>
        <input type="text" inputMode="numeric" pattern="\\d*" value={localEma2} onChange={(e) => setLocalEma2(e.target.value)} onBlur={commitEma2} placeholder="e.g. 26" />
      </label>

      <button disabled={!(symbolValid === true) || status === 'reloading'} title={!(symbolValid === true) ? '유효한 심볼을 입력하세요' : (status === 'reloading' ? '초기화 중...' : 'Start')} onClick={async () => {
        const ok = await validateSymbolOnce(symbol);
        if (!ok) { setSymbolValid(false); return; }
        setSymbolValid(true);
        // pass monitoring options to connect (App will accept and forward to server)
        try {
          // pass confirmCandles only if the parent provided it (App will default to 1 if omitted)
          connect(symbol, { interval: monitorMinutes, emaShort: monitorEma1, emaLong: monitorEma2 });
        } catch (e) {
          connect(symbol);
        }
      }}>Start</button>

      <button className="secondary" onClick={() => disconnect()}>Stop</button>

      {/* removed Auto-start and Debug checkboxes as requested */}

    </div>
  );
}

Controls.propTypes = {
  symbol: PropTypes.string.isRequired,
  setSymbol: PropTypes.func.isRequired,
  symbolValid: PropTypes.bool,
  setSymbolValid: PropTypes.func.isRequired,
  setSuggestions: PropTypes.func.isRequired,
  availableSymbols: PropTypes.array,
  validateSymbolOnce: PropTypes.func.isRequired,
  connect: PropTypes.func.isRequired,
  disconnect: PropTypes.func.isRequired,
  status: PropTypes.string,
  connected: PropTypes.bool,
  // monitoring props
  monitorMinutes: PropTypes.number.isRequired,
  setMonitorMinutes: PropTypes.func.isRequired,
  monitorEma1: PropTypes.number.isRequired,
  setMonitorEma1: PropTypes.func.isRequired,
  monitorEma2: PropTypes.number.isRequired,
  setMonitorEma2: PropTypes.func.isRequired,
  // monitorConfirm/setMonitorConfirm intentionally omitted so Confirm uses app default when not exposed in UI
};
