import React, { useEffect, useMemo, useRef, useState } from 'react';

// Simple EMA implementation: accepts array of numbers and period, returns array of EMA values (same length, first values may be null until seed)
function calculateEma(values, period) {
  if (!Array.isArray(values) || values.length === 0) return [];
  const k = 2 / (period + 1);
  const out = [];
  let ema = null;
  for (let i = 0; i < values.length; i++) {
    const v = Number(values[i]);
    if (isNaN(v)) { out.push(null); continue; }
    if (ema === null) {
      // seed with simple moving average of the first `period` values when enough
      if (i + 1 >= period) {
        const slice = values.slice(i + 1 - period, i + 1).map(Number);
        const s = slice.reduce((a, b) => a + b, 0) / period;
        ema = s;
        out.push(ema);
      } else {
        out.push(null);
      }
    } else {
      ema = v * k + ema * (1 - k);
      out.push(ema);
    }
  }
  return out;
}

function sleep(ms) { return new Promise((r) => setTimeout(r, ms)); }

export default function ScannerPage({ availableSymbols, fetchExchangeInfo }) {
  const [mode, setMode] = useState(null); // 'golden' | 'dead'
  const [running, setRunning] = useState(false);
  const [delayMs, setDelayMs] = useState(1000);
  const [results, setResults] = useState([]);
  const [progress, setProgress] = useState({ done: 0, total: 0 });
  const [currentSymbol, setCurrentSymbol] = useState(null);
  const cancelRef = useRef(false);
  const symbols = useMemo(() => (Array.isArray(availableSymbols) ? availableSymbols.slice() : []), [availableSymbols]);

  useEffect(() => {
    if ((!symbols || symbols.length === 0) && typeof fetchExchangeInfo === 'function') {
      try { fetchExchangeInfo(); } catch (e) {}
    }
  }, [symbols, fetchExchangeInfo]);

  function filteredSymbols() {
    if (!symbols || symbols.length === 0) return [];
    return symbols.filter(s => typeof s === 'string' && /USDT$/i.test(s));
  }

  async function scan(type) {
    const list = filteredSymbols();
    setResults([]);
    setProgress({ done: 0, total: list.length });
    cancelRef.current = false;
    setRunning(true);
    setMode(type);

    const endpointBase = 'https://fapi.binance.com/fapi/v1/klines';
    for (let i = 0; i < list.length; i++) {
      if (cancelRef.current) break;
      const sym = list[i];
      // show currently scanning symbol in the UI
      try { setCurrentSymbol(sym); } catch (e) {}
      try {
        const url = `${endpointBase}?symbol=${encodeURIComponent(sym)}&interval=5m&limit=500`;
        const r = await fetch(url);
        if (!r.ok) {
          if (r.status === 429) await sleep(Math.max(delayMs, 2000));
          setProgress((p) => ({ ...p, done: p.done + 1 }));
          await sleep(delayMs);
          continue;
        }
        const data = await r.json();
        const closes = data.map(d => parseFloat(d[4]));
        if (!Array.isArray(closes) || closes.length < 210) { setProgress((p) => ({ ...p, done: p.done + 1 })); await sleep(delayMs); continue; }
        const ema26 = calculateEma(closes, 26);
        const ema200 = calculateEma(closes, 200);
        const lastIdx = closes.length - 1;
        const prevIdx = lastIdx - 1;
        const prev26 = ema26[prevIdx];
        const prev200 = ema200[prevIdx];
        const last26 = ema26[lastIdx];
        const last200 = ema200[lastIdx];
        let matched = false;
        if (typeof prev26 === 'number' && typeof prev200 === 'number' && typeof last26 === 'number' && typeof last200 === 'number') {
          if (type === 'golden') { if (prev26 <= prev200 && last26 > last200) matched = true; }
          else if (type === 'dead') { if (prev26 >= prev200 && last26 < last200) matched = true; }
        }
        if (matched) { setResults((r) => [{ symbol: sym, last26, last200, time: new Date().toLocaleString() }, ...r]); }
      } catch (e) {}
      setProgress((p) => ({ ...p, done: p.done + 1 }));
      await sleep(delayMs);
    }

    setRunning(false);
    setMode(null);
    try { setCurrentSymbol(null); } catch (e) {}
  }

  function startGolden() { if (!running) scan('golden'); }
  function startDead() { if (!running) scan('dead'); }
  function stopScan() { cancelRef.current = true; setRunning(false); setMode(null); }

  return (
    <div className="alerts" style={{ padding: 8 }}>
      <div className="alerts-title">Scanner</div>
      <div className="scanner-controls" style={{ display: 'flex', gap: 8, alignItems: 'center', margin: '8px 0' }}>
        <button onClick={startGolden} disabled={running} className="scanner-btn green">Golden Cross</button>
        <button onClick={startDead} disabled={running} className="scanner-btn red">Dead Cross</button>
        <button onClick={stopScan} disabled={!running} className="scanner-btn stop">Stop</button>
        <div style={{ marginLeft: 'auto', display: 'flex', gap: 8, alignItems: 'center' }}>
          <label style={{ fontSize: '0.85rem', color: '#444' }}>Delay</label>
          <input type="number" min={200} step={100} value={delayMs} onChange={(e) => setDelayMs(Number(e.target.value || 1000))} style={{ width: 90 }} />
          <div style={{ fontSize: '0.85rem', color: '#666' }}>{progress.done}/{progress.total || filteredSymbols().length}</div>
        </div>
      </div>

      <div style={{ fontSize: '0.85rem', color: '#444', marginBottom: 8 }}>
        Scanning: {running ? (currentSymbol || '...') : 'idle'}
      </div>

      <ul className="alerts-list">
        {results.length === 0 ? (
          <li className="alert-item" style={{ color: '#666' }}>No matches yet.</li>
        ) : (
          results.map((r) => (
            <li key={r.symbol} className="alert-item">
              <div className="alert-left">
                <span className={`alert-indicator bull`} />
                <div className="alert-symbol">{r.symbol}</div>
              </div>
              <div className="alert-body">
                <div className="alert-info">
                  <div className="alert-type-short">{mode === 'golden' ? 'Golden' : 'Dead'}</div>
                  <div className="alert-time">{r.time}</div>
                </div>
                <div className="alert-right">
                  <div className="alert-price">{Number(r.last26).toFixed(6)} / {Number(r.last200).toFixed(6)}</div>
                </div>
              </div>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}
