import React from 'react';

function formatAge(ts) {
  if (!ts) return null;
  const diff = Math.max(0, Date.now() - ts);
  if (diff < 1000) return 'just now';
  if (diff < 60000) return `${Math.floor(diff / 1000)}s ago`;
  if (diff < 3600000) return `${Math.floor(diff / 60000)}m ago`;
  return `${Math.floor(diff / 3600000)}h ago`;
}

export default function Header({ title = 'Binance EMA Cross Alert', timeframe = '1m · EMA9 / EMA26', serverSymbol, sseConnected, lastHealthyAt, telegramConfigured, onNavigate, view }) {
  const age = formatAge(lastHealthyAt);
  const fresh = lastHealthyAt && (Date.now() - lastHealthyAt) < 30000; // fresh if <30s

  return (
    <div className="header">
      <div style={{display:'flex', alignItems:'center', gap:16}}>
        <div className="title">{title}</div>
        {/* simple menu bar */}
        <nav className="top-menu" style={{display:'flex', gap:8}}>
          <button className={`menu-btn ${view === 'alerts' ? 'active' : ''}`} onClick={() => onNavigate && onNavigate('alerts')}>Alerts</button>
          <button className={`menu-btn ${view === 'scanner' ? 'active' : ''}`} onClick={() => onNavigate && onNavigate('scanner')}>Scanner</button>
          <button className={`menu-btn ${view === 'debug' ? 'active' : ''}`} onClick={() => onNavigate && onNavigate('debug')}>Debug</button>
        </nav>
      </div>

      <div className="header-right">
        <div>{timeframe}</div>
        <div style={{ fontSize: '0.8rem', marginTop: 4 }}>
          <span style={{ marginRight: 8 }}>Server: {serverSymbol || 'n/a'}</span>
          <span style={{ color: sseConnected ? '#0a0' : '#a00' }}>{sseConnected ? 'SSE: connected' : 'SSE: disconnected'}</span>
        </div>
        <div style={{ fontSize: '0.75rem', marginTop: 6 }}>
          <span style={{ marginRight: 10 }}>Telegram: {typeof telegramConfigured === 'boolean' ? (telegramConfigured ? 'ok' : 'not configured') : 'unknown'}</span>
          <span style={{ color: fresh ? '#0a0' : '#a00' }}>{age ? `healthy ${age}` : 'no health'}</span>
        </div>
      </div>
    </div>
  );
}
