import React from 'react';

export default function Alerts({ events = [], removeAlertByTs, symbol, symbolValid, status, connect, disconnect, monitorMinutes, monitorEma1, monitorEma2, monitorConfirm }) {
  // Only show primary alert events (bull/bear). Hide telegram_send / telegram status events
  const normTarget = (symbol || '').toString().replace(/[^A-Za-z0-9]/g, '').toUpperCase();
  const visible = (events || []).filter((ev) => {
    try {
      if (!ev || !ev.type) return false;
      const t = (ev.type || '').toString();
      // hide auxiliary telegram events
      if (t.startsWith('telegram')) return false;
      // only show items for the currently-selected symbol
      const evSym = (ev.symbol || '').toString().replace(/[^A-Za-z0-9]/g, '').toUpperCase();
      if (normTarget && evSym !== normTarget) return false;
      // show only bull/bear or similar alert types
      return t === 'bull' || t === 'bear' || t === 'alert';
    } catch (e) {
      return false;
    }
  });

  if (!visible || visible.length === 0) return null;

  return (
    <div className="alerts">
      <div style={{display: 'flex', alignItems: 'center', gap: 8, justifyContent: 'space-between'}}>
        <div style={{display:'flex', alignItems:'center', gap:8}}>
          <div className="alerts-title">Alerts</div>
          <div className="monitor-badge">{`${monitorMinutes}m · EMA${monitorEma1}/${monitorEma2}`}</div>
        </div>
        <div style={{display: 'flex', gap: 8, alignItems: 'center'}}>
          <button
            onClick={async () => {
              try {
                if (!symbol) return;
                // forward monitoring params to connect
                await connect(symbol, { interval: monitorMinutes, emaShort: monitorEma1, emaLong: monitorEma2, confirmCandles: monitorConfirm });
              } catch (e) {}
            }}
            disabled={!(symbolValid === true) || status === 'reloading'}
            title={!(symbolValid === true) ? '유효한 심볼을 입력하세요' : (status === 'reloading' ? '초기화 중...' : 'Start')}
          >Start</button>

          <button className="secondary" onClick={() => { try { disconnect(); } catch (e) {} }}>Stop</button>
        </div>
      </div>

      <ul className="alerts-list">
        {visible.map((ev, i) => {
          // determine explicit direction first (ev.type or ev.alert)
          const dir = (ev && (ev.type === 'bull' || ev.type === 'bear')) ? ev.type : ((ev && (ev.alert === 'bull' || ev.alert === 'bear')) ? ev.alert : null);
          // determine price class: prefer explicit direction indicators, otherwise compare to previous event price
          let priceClass = 'alert-price';
          try {
            const priceNum = (typeof ev.price === 'number') ? ev.price : parseFloat(ev.price);
            if (dir === 'bull') priceClass += ' up';
            else if (dir === 'bear') priceClass += ' down';
            else {
                // find most-recent prior event for same symbol with a numeric price
                const prior = (events || []).slice().reverse().find((e) => {
                  try {
                    if (!e || !e.symbol || !e.ts) return false;
                    if ((e.symbol || '').toString() !== (ev.symbol || '').toString()) return false;
                    const p = (typeof e.price === 'number') ? e.price : parseFloat(e.price);
                    return !isNaN(p) && e.ts < ev.ts;
                  } catch (err) { return false; }
                });
                if (prior) {
                  const priorPrice = (typeof prior.price === 'number') ? prior.price : parseFloat(prior.price);
                  if (!isNaN(priceNum) && !isNaN(priorPrice)) {
                    priceClass += (priceNum >= priorPrice) ? ' up' : ' down';
                  }
                }
              }
            } catch (err) {
              // ignore and fall back to default class
            }

          return (
          <li key={ev?.ts ?? i} className="alert-item">
            <div className="alert-left">
              <span className={`alert-indicator ${dir === 'bull' ? 'bull' : 'bear'}`} />
              <div className="alert-symbol">{ev.symbol || ''}</div>
            </div>

            <div className="alert-body">
              <div className="alert-info">
                <div className="alert-type-short">{dir === 'bull' ? 'Bull' : (dir === 'bear' ? 'Bear' : (ev.type === 'bull' ? 'Bull' : 'Bear'))}</div>
                <div className="alert-time">{ev.time}</div>
              </div>
              <div className="alert-right">
                <div className={priceClass}>{typeof ev.price === 'number' ? ev.price : ev.price}</div>
                <div className="alert-source">{ev.source || ''}</div>
                <button className="delete-btn" onClick={() => removeAlertByTs(ev.ts)} aria-label="삭제" title="삭제">✕</button>
              </div>
            </div>
          </li>
          );
        })}
      </ul>
    </div>
  );
}
