import React, { createContext, useContext } from 'react';
import useEmaCross from '../hooks/useEmaCross';

const EmaContext = createContext(null);

export function EmaProvider({ symbol = 'BTCUSDT', autoConnect = true, debug = false, children }) {
  // Keep a single instance of the EMA hook running for the whole app.
  const ema = useEmaCross({ symbol, autoConnect, debug });
  return (
    <EmaContext.Provider value={ema}>
      {children}
    </EmaContext.Provider>
  );
}

export function useEma() {
  const ctx = useContext(EmaContext);
  if (!ctx) throw new Error('useEma must be used within an EmaProvider');
  return ctx;
}

export default EmaContext;
