import React, { Suspense, useEffect, useMemo, useState } from 'react';
import './App.css';
import { useEma } from './contexts/EmaContext';
import Alerts from './components/Alerts';
import Metrics from './components/Metrics';
import Controls from './components/Controls';
import DebugPanel from './components/DebugPanel';
import Notes from './components/Notes';

const ScannerPage = React.lazy(() => import('./pages/ScannerPage'));

function AppMain() {
  // simple page switch: "alerts" (main) or "scanner"
  const [page, setPage] = useState('alerts');
  const [showDebug, setShowDebug] = useState(false);

  // consume shared EMA hook/context
  const ema = useEma();

  // persist last selected symbol to localStorage
  useEffect(() => {
    if (!ema) return;
    const key = 'lastSymbol';
    const saved = localStorage.getItem(key);
    if (saved && !ema.activeSymbol) {
      ema.setActiveSymbol(saved);
    }
    const handle = () => {
      if (ema.activeSymbol) localStorage.setItem(key, ema.activeSymbol);
    };
    // listen to changes via polling on activeSymbol (no event emitter here)
    const id = setInterval(handle, 1000);
    return () => clearInterval(id);
  }, [ema]);

  const header = useMemo(() => '5m · EMA26 / EMA200', []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>{header}</h1>
        <nav>
          <button onClick={() => setPage('alerts')}>Alerts</button>
          <button onClick={() => setPage('scanner')}>Scanner</button>
          <label style={{ marginLeft: 12 }}>
            <input type="checkbox" checked={showDebug} onChange={(e) => setShowDebug(e.target.checked)} />
            Debug
          </label>
        </nav>
      </header>

      <main style={{ padding: 12 }}>
        {page === 'alerts' && (
          <>
            <div style={{ display: 'flex', gap: 12 }}>
              <div style={{ flex: 1 }}>
                <Metrics ema={ema} />
                <Controls ema={ema} />
              </div>
              <div style={{ width: 380 }}>
                <Alerts ema={ema} />
                <Notes />
              </div>
            </div>
            {showDebug && <DebugPanel ema={ema} />}
          </>
        )}

        {page === 'scanner' && (
          <Suspense fallback={<div>Loading scanner...</div>}>
            <ScannerPage />
          </Suspense>
        )}
      </main>
    </div>
  );
}

export default AppMain;
