import React from 'react';

function formatNumberForDisplay(v) {
  if (typeof v !== 'number' || Number.isNaN(v)) return '—';
  // show more precision for small-price assets
  if (Math.abs(v) < 1) return v.toFixed(6);
  if (Math.abs(v) < 100) return v.toFixed(4);
  return v.toFixed(2);
}

export default function Metrics({ activeSymbol, symbol, lastPrice, lastTick, lastCandleClosed, cross, confirmedCross, ema9, ema26 }) {
  const displayPrice = (activeSymbol === symbol)
    ? (typeof lastTick !== 'undefined' && lastTick !== null ? formatNumberForDisplay(lastTick) : (lastPrice != null ? formatNumberForDisplay(lastPrice) : '—'))
    : '—';

  return (
    <div className="metrics">
      <div className="metric">
        <div className="metric-label">
          <span>Last price</span>
          {activeSymbol === symbol ? (
            lastCandleClosed ? (
              <span className="badge closed">CLOSED</span>
            ) : (
              <span className="badge open">LIVE</span>
            )
          ) : null}
        </div>
        <div>{displayPrice}</div>
      </div>
      <div className="metric"><div className="metric-label">Cross (confirmed)</div><div>{activeSymbol === symbol ? (confirmedCross ?? '—') : '—'}</div></div>
      <div className="metric"><div className="metric-label">EMA9</div><div>{activeSymbol === symbol && ema9 ? formatNumberForDisplay(ema9) : '—'}</div></div>
      <div className="metric"><div className="metric-label">EMA26</div><div>{activeSymbol === symbol && ema26 ? formatNumberForDisplay(ema26) : '—'}</div></div>
    </div>
  );
}
