import React from 'react';

export default function TopMenu({ onNavigate, view, theme = 'light', setTheme }) {
  return (
    <div className="top-menu-bar" style={{width: '100%', display: 'flex', justifyContent: 'center', padding: '10px 0', background: 'transparent'}}>
      <div style={{maxWidth: 760, width: '100%', display: 'flex', gap: 8, justifyContent: 'center', alignItems: 'center'}}>
        <div style={{display: 'flex', gap: 8, justifyContent: 'center', flex: 1}}>
          <button className={`menu-btn ${view === 'alerts' ? 'active' : ''}`} onClick={() => onNavigate && onNavigate('alerts')}>Alerts</button>
          <button className={`menu-btn ${view === 'scanner' ? 'active' : ''}`} onClick={() => onNavigate && onNavigate('scanner')}>Scanner</button>
        </div>
        <div style={{display: 'flex', alignItems: 'center', gap: 8}}>
          <select aria-label="Theme" value={theme} onChange={(e) => { try { setTheme && setTheme(e.target.value); } catch (e) {} }} style={{ padding: '6px 8px', borderRadius: 8, border: '1px solid transparent', background: 'transparent', color: 'inherit' }}>
            <option value="light">Light</option>
            <option value="vscode">VSCode Dark</option>
            <option value="github">GitHub Dark</option>
          </select>
        </div>
      </div>
    </div>
  );
}
