import './App.css';
import React, { useEffect, useRef, useState } from 'react';
import useEmaCross from './hooks/useEmaCross';

function beep() {
  try {
    const AudioCtx = window.AudioContext || window.webkitAudioContext;
    const ctx = new AudioCtx();
    const o = ctx.createOscillator();
    const g = ctx.createGain();
    o.type = 'sine';
    o.frequency.value = 880;
    o.connect(g);
    g.connect(ctx.destination);
    o.start();
    g.gain.setValueAtTime(0.0001, ctx.currentTime);
    g.gain.exponentialRampToValueAtTime(0.1, ctx.currentTime + 0.01);
    g.gain.exponentialRampToValueAtTime(0.0001, ctx.currentTime + 0.6);
    setTimeout(() => {
      o.stop();
      try { ctx.close(); } catch (e) {}
    }, 700);
  } catch (e) {
    // ignore
  }
}

function App() {
  const [symbol, setSymbol] = useState('BTCUSDT');
  const [autoStart, setAutoStart] = useState(true);
  const [symbolValid, setSymbolValid] = useState(null); // null=unknown, true/false
  const symbolValidateTimer = useRef(null);
  const [availableSymbols, setAvailableSymbols] = useState(null);
  const [showDebug, setShowDebug] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  // helper to fetch exchangeInfo and populate availableSymbols
  async function fetchExchangeInfo() {
    try {
      const res = await fetch('https://api.binance.com/api/v3/exchangeInfo');
      if (!res.ok) {
        console.warn('exchangeInfo fetch failed', res.status);
        setAvailableSymbols([]);
        return;
      }
      const data = await res.json();
      const syms = (data.symbols || []).map(s => s.symbol);
      setAvailableSymbols(syms);
    } catch (e) {
      console.warn('exchangeInfo fetch error', e);
      setAvailableSymbols([]);
    }
  }

  // fetch full symbol list once on mount for reliable validation
  useEffect(() => {
    fetchExchangeInfo();
  }, []);

  // helper to validate a symbol (returns true/false)
  async function validateSymbolOnce(s) {
    const q = (s || '').replace(/[^A-Za-z0-9]/g, '').toUpperCase();
    if (!q) return false;
    // If we have a cached symbol list, compare using a normalized form (strip non-alnum, uppercase)
    if (availableSymbols && availableSymbols.length > 0) {
      const normQ = q;
      for (const sym of availableSymbols) {
        const normSym = (sym || '').replace(/[^A-Za-z0-9]/g, '').toUpperCase();
        if (normSym === normQ) return true;
      }
      return false;
    }
    // Fallback to exchangeInfo query for the single symbol
    try {
      const res = await fetch(`https://api.binance.com/api/v3/exchangeInfo?symbol=${q}`);
      if (!res.ok) return false;
      const data = await res.json();
      // if API returned an object with matching symbol(s), accept it
      if (data && Array.isArray(data.symbols) && data.symbols.length > 0) return true;
      if (data && data.symbol) return true;
      return false;
    } catch (e) {
      return false;
    }
  }

  // if symbol becomes invalid, disable autoStart and disconnect to avoid wrong subscriptions
  // (moved below after hook initialization because it depends on connected/disconnect)
  const { ema26, ema200, lastPrice, lastCandleClosed, cross, connected, status, connect, disconnect, activeSymbol } = useEmaCross({ symbol, autoConnect: autoStart, debug: showDebug });

  // 자동 연결: symbol이 유효하고 autoStart가 켜져 있으면 Start 버튼을 누르지 않아도 connect 호출
  useEffect(() => {
    try {
      const q = (symbol || '').replace(/[^A-Za-z0-9]/g, '').toUpperCase();
      if (q && symbolValid === true && autoStart) {
        // avoid calling connect if already active for this symbol
        if (!activeSymbol || activeSymbol.toString().toUpperCase() !== q) {
          connect(q);
        }
      }
    } catch (e) {
      // ignore
    }
  }, [symbol, symbolValid, autoStart, activeSymbol, connect]);
  const [events, setEvents] = useState([]);
  const lastNotified = useRef(null);

  // if symbol becomes invalid, disable autoStart and disconnect to avoid wrong subscriptions
  useEffect(() => {
    if (symbolValid === false) {
      if (autoStart) setAutoStart(false);
      // ensure disconnected
      if (connected) disconnect();
    }
    // include dependencies to avoid stale closures
  }, [symbolValid, autoStart, connected, disconnect]);

  // Ask for notification permission on first user interaction
  useEffect(() => {
    const handle = () => {
      if ('Notification' in window && Notification.permission === 'default') {
        Notification.requestPermission().then(() => {});
      }
      window.removeEventListener('click', handle);
    };
    window.addEventListener('click', handle);
    return () => window.removeEventListener('click', handle);
  }, []);

  // react to cross changes and notify (skip initial)
  useEffect(() => {
    // only notify on confirmed (closed) candles
    if (!cross || !lastCandleClosed) return;
    if (lastNotified.current == null) {
      // seed without notifying
      lastNotified.current = cross;
      return;
    }

    if (lastNotified.current !== cross) {
      const type = cross === 'bull' ? 'Bullish EMA26 > EMA200' : 'Bearish EMA26 < EMA200';
      const body = `${symbol} ${type} @ ${lastPrice ?? 'N/A'}`;

      // browser notification
      if ('Notification' in window && Notification.permission === 'granted') {
        new Notification(type, { body });
      }

      // beep and log
      beep();
      setEvents((s) => [{ time: new Date().toLocaleString(), type: cross, price: lastPrice }, ...s].slice(0, 20));
      lastNotified.current = cross;
    }
  }, [cross, lastPrice, lastCandleClosed, symbol]);

  return (
    <div className="App">
      <div className="container">
        <div className="card">
          <div className="header">
            <div className="title">Binance EMA Cross Alert</div>
            <div style={{ marginLeft: 'auto', fontSize: 12, color: '#6b7280' }}>5m · EMA26 / EMA200</div>
          </div>

          <div className="controls">
            <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
              <span style={{ fontSize: 13 }}>Symbol</span>
              <input type="text" value={symbol} onChange={(e) => {
                const v = e.target.value.toUpperCase();
                setSymbol(v);
                // reset validation state while typing
                setSymbolValid(null);
                if (symbolValidateTimer.current) clearTimeout(symbolValidateTimer.current);
                symbolValidateTimer.current = setTimeout(() => {
                  try {
                    const q = v.replace(/[^A-Za-z0-9]/g, '').toUpperCase();
                    const pool = availableSymbols || [];
                    if (pool.length > 0) {
                      const normQ = q;
                      // try to find exact match using normalized comparison
                      const exact = pool.some(sy => (sy || '').replace(/[^A-Za-z0-9]/g, '').toUpperCase() === normQ) || pool.includes(q);
                      if (exact) {
                        setSymbolValid(true);
                        setSuggestions([]);
                        // if autoStart is enabled, start monitoring immediately for this symbol
                        try {
                          if (autoStart) {
                            connect(q);
                          }
                        } catch (e) {
                          // connect may be stale during initial render; ignore
                        }
                        return;
                      }
                      const found = pool.filter(s => s.includes(q) || s.startsWith(q) || q.startsWith(s)).slice(0, 8);
                      setSuggestions(found);
                      // If we have candidate suggestions, treat validation as "unknown" while the user picks one.
                      // Only mark invalid if there are no suggestions and it's not an exact match.
                      setSymbolValid(found.length > 0 ? null : false);
                      return;
                    }
                    // fallback: try direct exchangeInfo query
                    (async () => {
                      try {
                        const url = `https://api.binance.com/api/v3/exchangeInfo?symbol=${q}`;
                        const res = await fetch(url);
                        if (!res.ok) {
                          setSymbolValid(false);
                          return;
                        }
                        const data = await res.json();
                        if (data && (data.symbols || data.symbol)) {
                          setSymbolValid(true);
                        } else {
                          setSymbolValid(false);
                        }
                      } catch (err) {
                        setSymbolValid(false);
                      }
                    })();
                  } catch (err) {
                    setSymbolValid(false);
                    setSuggestions([]);
                  }
                }, 600);
              }} />
            </label>
            <button disabled={!(symbolValid === true) || status === 'reloading'} title={!(symbolValid === true) ? '유효한 심볼을 입력하세요' : (status === 'reloading' ? '초기화 중...' : 'Start')} onClick={async () => {
              // validate before connecting
              const ok = await validateSymbolOnce(symbol);
              if (!ok) {
                setSymbolValid(false);
                return;
              }
              setSymbolValid(true);
              connect(symbol);
            }}>Start</button>
            <button className="secondary" onClick={() => disconnect()}>Stop</button>
            <label style={{ marginLeft: 12, display: 'flex', alignItems: 'center', gap: 8 }}>
              <input type="checkbox" checked={autoStart} onChange={(e) => {
                const val = e.target.checked;
                setAutoStart(val);
                // if disabling auto start, disconnect immediately
                if (!val) disconnect();
                // if enabling and initialized, connect
                if (val && status === 'initialized' && !connected) connect();
              }} />
              <span style={{ fontSize: 13 }}>Auto-start</span>
            </label>
            <label style={{ marginLeft: 12, display: 'flex', alignItems: 'center', gap: 8 }}>
              <input type="checkbox" checked={showDebug} onChange={(e) => setShowDebug(e.target.checked)} />
              <span style={{ fontSize: 13 }}>Debug</span>
            </label>
          </div>
          {/* 간단한 로딩 표시: 심볼 변경으로 상태가 reloading일 때 보임 */}
          {status === 'reloading' && (
            <div style={{ marginTop: 8, color: '#6b7280', fontSize: 13 }}>초기화 중... 잠시만 기다려주세요</div>
          )}
          {symbolValid === false && (
            <div style={{ color: '#dc2626', marginTop: 8 }}>
              Invalid symbol — please check the trading pair (e.g. BTCUSDT)
              {suggestions && suggestions.length > 0 && (
                <div style={{ marginTop: 8 }}>
                  <div style={{ fontSize: 13, marginBottom: 6, color: '#374151' }}>Did you mean:</div>
                  <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                    {suggestions.map((s) => (
                      <button key={s} onClick={() => {
                        setSymbol(s);
                        setSymbolValid(true);
                        setSuggestions([]);
                        try { if (autoStart) connect(s); } catch (e) {}
                      }} style={{ padding: '6px 8px', borderRadius: 6, border: '1px solid #e6e9ef', background: '#fff', cursor: 'pointer' }}>{s}</button>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}

          {showDebug && (
            <div style={{ marginTop: 12, padding: 10, border: '1px dashed #e6e9ef', borderRadius: 8, background: '#fff' }}>
              <div style={{ fontSize: 13, color: '#374151', marginBottom: 6 }}>Debug info</div>
              <div style={{ fontSize: 13, color: '#6b7280' }}>Available symbols: {availableSymbols ? availableSymbols.length : 'loading'}</div>
              <div style={{ marginTop: 8, fontSize: 13 }}>
                Normalized match for <strong>{symbol}</strong>: {
                  (() => {
                    try {
                      if (!availableSymbols || availableSymbols.length === 0) return 'no data';
                      const q = (symbol || '').replace(/[^A-Za-z0-9]/g, '').toUpperCase();
                      const found = availableSymbols.some(sy => (sy || '').replace(/[^A-Za-z0-9]/g, '').toUpperCase() === q);
                      return found ? 'YES' : 'NO';
                    } catch (e) { return 'err'; }
                  })()
                }
              </div>
              <div style={{ marginTop: 8, display: 'flex', gap: 8 }}>
                <button onClick={() => fetchExchangeInfo()} style={{ padding: '6px 8px' }}>Reload symbols</button>
                <button onClick={() => console.log('availableSymbols sample', (availableSymbols || []).slice(0, 50))} style={{ padding: '6px 8px' }}>Log sample</button>
              </div>
            </div>
          )}

          <div className="status">
            Status: <strong>{status}</strong> {connected ? <span style={{ color: '#059669' }}>(connected)</span> : <span style={{ color: '#6b7280' }}>(disconnected)</span>}
          </div>

          <div className="metrics">
            <div className="metric">
              <div style={{ fontSize: 12, color: '#6b7280', display: 'flex', alignItems: 'center', gap: 8 }}>
                <span>Last price</span>
                {activeSymbol === symbol ? (
                  lastCandleClosed ? (
                    <span className="badge closed">CLOSED</span>
                  ) : (
                    <span className="badge open">LIVE</span>
                  )
                ) : null}
              </div>
              <div>{activeSymbol === symbol ? (lastPrice ?? '—') : '—'}</div>
            </div>
            <div className="metric"><div style={{ fontSize: 12, color: '#6b7280' }}>Cross</div><div>{activeSymbol === symbol ? (cross ?? '—') : '—'}</div></div>
            <div className="metric"><div style={{ fontSize: 12, color: '#6b7280' }}>EMA26</div><div>{activeSymbol === symbol && ema26 ? ema26.toFixed(4) : '—'}</div></div>
            <div className="metric"><div style={{ fontSize: 12, color: '#6b7280' }}>EMA200</div><div>{activeSymbol === symbol && ema200 ? ema200.toFixed(4) : '—'}</div></div>
          </div>

          <div className="alerts">
            <div style={{ fontSize: 14, fontWeight: 600 }}>Alerts</div>
            {events.length === 0 ? <div style={{ marginTop: 8, color: '#6b7280' }}>No alerts yet</div> : (
              <ul>
                {events.map((ev, i) => (
                  <li key={i} style={{ padding: '6px 0', borderBottom: '1px solid #f1f5f9' }}>{ev.time} — {ev.type.toUpperCase()} @ {ev.price}</li>
                ))}
              </ul>
            )}
          </div>

          <div className="note">Notes: This app uses Binance public REST + websocket. Make sure network allows wss access to stream.binance.com.</div>
        </div>
      </div>
    </div>
  );
}

export default App;
