import React from 'react';

function makeBinanceUrl(sym) {
  try {
    if (!sym) return 'https://www.binance.com';
    const s = String(sym).toUpperCase().replace(/[^A-Z0-9]/g, '');
    const quotes = ['USDT','BUSD','USDC','BTC','ETH','BNB','TRY','EUR','BRL','GBP','AUD','RUB','TUSD','PAX','USDS','BKRW'];
    for (const q of quotes) {
      if (s.endsWith(q) && s.length > q.length) {
        const base = s.slice(0, s.length - q.length);
        return `https://www.binance.com/en/trade/${base}_${q}`;
      }
    }
    return `https://www.binance.com/en/trade/${s}`;
  } catch (e) {
    return 'https://www.binance.com';
  }
}

// Try to open native Binance app via protocol handler, fallback to web URL
function openBinanceAppOrWeb(sym) {
  try {
    const web = makeBinanceUrl(sym);
    // derive base_quote like makeBinanceUrl does
    const s = String(sym || '').toUpperCase().replace(/[^A-Z0-9]/g, '');
    const quotes = ['USDT','BUSD','USDC','BTC','ETH','BNB','TRY','EUR','BRL','GBP','AUD','RUB','TUSD','PAX','USDS','BKRW'];
    let protoPath = null;
    for (const q of quotes) {
      if (s.endsWith(q) && s.length > q.length) {
        const base = s.slice(0, s.length - q.length);
        protoPath = `${base}_${q}`;
        break;
      }
    }
    if (!protoPath && s) protoPath = s;

    // custom protocol for Binance desktop (best-effort). If app registers binance://, this will open it.
    const proto = protoPath ? `binance://trade/${protoPath}` : null;

    if (proto) {
      // User interaction required; invoke via temporary link to respect popup blockers.
      const a = document.createElement('a');
      a.href = proto;
      a.style.display = 'none';
      document.body.appendChild(a);
      a.click();
      // fallback to web after a short delay
      setTimeout(() => {
        try { window.open(web, '_blank', 'noopener,noreferrer'); } catch (e) { /* ignore */ }
        try { document.body.removeChild(a); } catch (e) {}
      }, 800);
      return;
    }

    // no proto possible, just open web
    window.open(web, '_blank', 'noopener,noreferrer');
  } catch (e) {
    try { window.open(makeBinanceUrl(sym), '_blank', 'noopener,noreferrer'); } catch (ee) {}
  }
}

export default function Alerts({ events = [], removeAlertByTs, symbol, symbolValid, status, connect, disconnect, monitorMinutes, monitorEma1, monitorEma2 }) {
  // Only show primary alert events (bull/bear). Hide telegram_send / telegram status events
  const normTarget = (symbol || '').toString().replace(/[^A-Za-z0-9]/g, '').toUpperCase();
  const visible = (events || []).filter((ev) => {
    try {
      if (!ev || !ev.type) return false;
      const t = (ev.type || '').toString();
      // hide auxiliary telegram events
      if (t.startsWith('telegram')) return false;
      // only show items for the currently-selected symbol
      const evSym = (ev.symbol || '').toString().replace(/[^A-Za-z0-9]/g, '').toUpperCase();
      if (normTarget && evSym !== normTarget) return false;
      // show only bull/bear or similar alert types
      return t === 'bull' || t === 'bear' || t === 'alert';
    } catch (e) {
      return false;
    }
  });

  if (!visible || visible.length === 0) return null;

  return (
    <div className="alerts">
      <div style={{display: 'flex', alignItems: 'center', gap: 8, justifyContent: 'space-between'}}>
        <div style={{display:'flex', alignItems:'center', gap:8}}>
          <div className="alerts-title">Alerts</div>
          <div className="monitor-badge">{`${monitorMinutes}m · EMA${monitorEma1}/${monitorEma2}`}</div>
        </div>
        <div style={{display: 'flex', gap: 8, alignItems: 'center'}}>
          <button
            onClick={async () => {
              try {
                if (!symbol) return;
                // forward monitoring params to connect
                await connect(symbol, { interval: monitorMinutes, emaShort: monitorEma1, emaLong: monitorEma2 });
              } catch (e) {}
            }}
            disabled={!(symbolValid === true) || status === 'reloading'}
            title={!(symbolValid === true) ? '유효한 심볼을 입력하세요' : (status === 'reloading' ? '초기화 중...' : 'Start')}
          >Start</button>

          <button className="secondary" onClick={() => { try { disconnect(); } catch (e) {} }}>Stop</button>
        </div>
      </div>

      <ul className="alerts-list">
        {visible.map((ev, i) => (
          <li key={ev?.ts ?? i} className="alert-item">
            <div className="alert-left">
              <span className={`alert-indicator ${ev.type === 'bull' ? 'bull' : 'bear'}`} />
              <a
                className="alert-symbol"
                href={makeBinanceUrl(ev.symbol)}
                onClick={(e) => { try { e.preventDefault(); openBinanceAppOrWeb(ev.symbol); } catch (err) {} }}
                target="_blank"
                rel="noopener noreferrer"
              >{ev.symbol || ''}</a>
            </div>

            <div className="alert-body">
              <div className="alert-info">
                <div className="alert-type-short">{ev.type === 'bull' ? 'Bull' : 'Bear'}</div>
                <div className="alert-time">{ev.time}</div>
              </div>
              <div className="alert-right">
                <div className="alert-price">{typeof ev.price === 'number' ? ev.price : ev.price}</div>
                <div className="alert-source">{ev.source || ''}</div>
                <button className="delete-btn" onClick={() => removeAlertByTs(ev.ts)} aria-label="삭제" title="삭제">✕</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
