// Lightweight EMA utilities
// EMA utilities removed
// The original calculateInitialEMA/updateEMA implementations were removed
// intentionally per user request. These stubs will warn at runtime and
// return null so that any calling code sees that EMA calculations are
// disabled. Re-implement these functions when you want to restore EMA logic.
export function sma(values) {
  console.warn('[utils/ema] sma() called but EMA utilities were removed');
  return null;
}

export function calculateInitialEMA(values, period) {
  console.warn('[utils/ema] calculateInitialEMA() called but EMA utilities were removed');
  return null;
}

export function updateEMA(prevEMA, price, period) {
  console.warn('[utils/ema] updateEMA() called but EMA utilities were removed');
  return null;
}

const ema = { sma, calculateInitialEMA, updateEMA };
export default ema;
