import './App.css';
import React, { useEffect, useRef, useState } from 'react';
import useEmaCross from './hooks/useEmaCross';

function beep() {
  try {
    const AudioCtx = window.AudioContext || window.webkitAudioContext;
    const ctx = new AudioCtx();
    const o = ctx.createOscillator();
    const g = ctx.createGain();
    o.type = 'sine';
    o.frequency.value = 880;
    o.connect(g);
    g.connect(ctx.destination);
    o.start();
    g.gain.setValueAtTime(0.0001, ctx.currentTime);
    g.gain.exponentialRampToValueAtTime(0.1, ctx.currentTime + 0.01);
    g.gain.exponentialRampToValueAtTime(0.0001, ctx.currentTime + 0.6);
    setTimeout(() => {
      o.stop();
      try { ctx.close(); } catch (e) {}
    }, 700);
  } catch (e) {
    // ignore
  }
}

function App() {
  const [symbol, setSymbol] = useState('BTCUSDT');
  const [autoStart, setAutoStart] = useState(true);
  const [symbolValid, setSymbolValid] = useState(null); // null=unknown, true/false
  const symbolValidateTimer = useRef(null);
  const [availableSymbols, setAvailableSymbols] = useState(null);
  const [suggestions, setSuggestions] = useState([]);
  const { ema26, ema200, lastPrice, lastCandleClosed, cross, connected, status, connect, disconnect } = useEmaCross({ symbol, autoConnect: autoStart });
  const [events, setEvents] = useState([]);
  const lastNotified = useRef(null);

  // Ask for notification permission on first user interaction
  useEffect(() => {
    const handle = () => {
      if ('Notification' in window && Notification.permission === 'default') {
        Notification.requestPermission().then(() => {});
      }
      window.removeEventListener('click', handle);
    };
    window.addEventListener('click', handle);
    return () => window.removeEventListener('click', handle);
  }, []);

  // react to cross changes and notify (skip initial)
  useEffect(() => {
    // only notify on confirmed (closed) candles
    if (!cross || !lastCandleClosed) return;
    if (lastNotified.current == null) {
      // seed without notifying
      lastNotified.current = cross;
      return;
    }

    if (lastNotified.current !== cross) {
      const type = cross === 'bull' ? 'Bullish EMA26 > EMA200' : 'Bearish EMA26 < EMA200';
      const body = `${symbol} ${type} @ ${lastPrice ?? 'N/A'}`;

      // browser notification
      if ('Notification' in window && Notification.permission === 'granted') {
        new Notification(type, { body });
      }

      // beep and log
      beep();
      setEvents((s) => [{ time: new Date().toLocaleString(), type: cross, price: lastPrice }, ...s].slice(0, 20));
      lastNotified.current = cross;
    }
  }, [cross, lastPrice, lastCandleClosed, symbol]);

  return (
    <div className="App">
      <div className="container">
        <div className="card">
          <div className="header">
            <div className="title">Binance EMA Cross Alert</div>
            <div style={{ marginLeft: 'auto', fontSize: 12, color: '#6b7280' }}>5m · EMA26 / EMA200</div>
          </div>

          <div className="controls">
            <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
              <span style={{ fontSize: 13 }}>Symbol</span>
              <input type="text" value={symbol} onChange={(e) => {
                const v = e.target.value.toUpperCase();
                setSymbol(v);
                // reset validation state while typing
                setSymbolValid(null);
                if (symbolValidateTimer.current) clearTimeout(symbolValidateTimer.current);
                symbolValidateTimer.current = setTimeout(async () => {
                  try {
                    // check exchangeInfo for symbol existence (direct query)
                    const url = `https://api.binance.com/api/v3/exchangeInfo?symbol=${v}`;
                    const res = await fetch(url);
                    if (res.ok) {
                      const data = await res.json();
                      if (data && (data.symbols || data.symbol)) {
                        setSymbolValid(true);
                        setSuggestions([]);
                        return;
                      }
                    }

                    // direct check failed -> fetch full symbol list once and compute suggestions
                    if (!availableSymbols) {
                      try {
                        const allRes = await fetch('https://api.binance.com/api/v3/exchangeInfo');
                        if (allRes.ok) {
                          const allData = await allRes.json();
                          const syms = (allData.symbols || []).map(s => s.symbol);
                          setAvailableSymbols(syms);
                        }
                      } catch (e) {
                        // ignore
                      }
                    }

                    const pool = availableSymbols || [];
                    const q = v.replace(/[^A-Za-z0-9]/g, '').toUpperCase();
                    const found = pool.filter(s => s.includes(q) || s.startsWith(q) || q.startsWith(s)).slice(0, 8);
                    setSuggestions(found);
                    setSymbolValid(found.length > 0 ? false : false);
                  } catch (err) {
                    setSymbolValid(false);
                    setSuggestions([]);
                  }
                }, 600);
              }} />
            </label>
            <button onClick={() => connect()}>Start</button>
            <button className="secondary" onClick={() => disconnect()}>Stop</button>
            <label style={{ marginLeft: 12, display: 'flex', alignItems: 'center', gap: 8 }}>
              <input type="checkbox" checked={autoStart} onChange={(e) => {
                const val = e.target.checked;
                setAutoStart(val);
                // if disabling auto start, disconnect immediately
                if (!val) disconnect();
                // if enabling and initialized, connect
                if (val && status === 'initialized' && !connected) connect();
              }} />
              <span style={{ fontSize: 13 }}>Auto-start</span>
            </label>
          </div>
          {symbolValid === false && (
            <div style={{ color: '#dc2626', marginTop: 8 }}>
              Invalid symbol — please check the trading pair (e.g. BTCUSDT)
              {suggestions && suggestions.length > 0 && (
                <div style={{ marginTop: 8 }}>
                  <div style={{ fontSize: 13, marginBottom: 6, color: '#374151' }}>Did you mean:</div>
                  <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                    {suggestions.map((s) => (
                      <button key={s} onClick={() => {
                        setSymbol(s);
                        setSymbolValid(true);
                        setSuggestions([]);
                      }} style={{ padding: '6px 8px', borderRadius: 6, border: '1px solid #e6e9ef', background: '#fff', cursor: 'pointer' }}>{s}</button>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}

          <div className="status">
            Status: <strong>{status}</strong> {connected ? <span style={{ color: '#059669' }}>(connected)</span> : <span style={{ color: '#6b7280' }}>(disconnected)</span>}
          </div>

          <div className="metrics">
            <div className="metric">
              <div style={{ fontSize: 12, color: '#6b7280', display: 'flex', alignItems: 'center', gap: 8 }}>
                <span>Last price</span>
                {lastCandleClosed ? (
                  <span className="badge closed">CLOSED</span>
                ) : (
                  <span className="badge open">LIVE</span>
                )}
              </div>
              <div>{lastPrice ?? '—'}</div>
            </div>
            <div className="metric"><div style={{ fontSize: 12, color: '#6b7280' }}>Cross</div><div>{cross ?? '—'}</div></div>
            <div className="metric"><div style={{ fontSize: 12, color: '#6b7280' }}>EMA26</div><div>{ema26 ? ema26.toFixed(4) : '—'}</div></div>
            <div className="metric"><div style={{ fontSize: 12, color: '#6b7280' }}>EMA200</div><div>{ema200 ? ema200.toFixed(4) : '—'}</div></div>
          </div>

          <div className="alerts">
            <div style={{ fontSize: 14, fontWeight: 600 }}>Alerts</div>
            {events.length === 0 ? <div style={{ marginTop: 8, color: '#6b7280' }}>No alerts yet</div> : (
              <ul>
                {events.map((ev, i) => (
                  <li key={i} style={{ padding: '6px 0', borderBottom: '1px solid #f1f5f9' }}>{ev.time} — {ev.type.toUpperCase()} @ {ev.price}</li>
                ))}
              </ul>
            )}
          </div>

          <div className="note">Notes: This app uses Binance public REST + websocket. Make sure network allows wss access to stream.binance.com.</div>
        </div>
      </div>
    </div>
  );
}

export default App;
