import React, { useEffect, useState } from 'react';

function formatNumberForDisplay(v) {
  if (typeof v !== 'number' || Number.isNaN(v)) return '—';
  // show more precision for small-price assets
  if (Math.abs(v) < 1) return v.toFixed(6);
  if (Math.abs(v) < 100) return v.toFixed(4);
  return v.toFixed(2);
}

export default function Metrics({ activeSymbol, symbol, lastPrice, lastTick, lastCandleClosed, cross, confirmedCross, ema9, ema26, monitorEma1 = 9, monitorEma2 = 26 }) {
  const displayPrice = (activeSymbol === symbol)
    ? (typeof lastTick !== 'undefined' && lastTick !== null ? formatNumberForDisplay(lastTick) : (lastPrice != null ? formatNumberForDisplay(lastPrice) : '—'))
    : '—';

  // determine class for displayed price: when LIVE, compare live tick to last closed price
  let priceClass = 'metric-price';
  // daily direction override (1d candle) - prefer this when available
  const [dailyDir, setDailyDir] = useState(null);
  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const serverUrl = process.env.REACT_APP_SERVER_URL || 'http://localhost:3001';
        const q = (symbol || '').toString().replace(/[^A-Za-z0-9]/g, '').toUpperCase();
        if (!q) return;
        const r = await fetch(`${serverUrl}/candles?symbol=${encodeURIComponent(q)}&interval=1d&limit=1`);
        if (!mounted) return;
        if (!r.ok) return;
        const j = await r.json();
        if (!j || !Array.isArray(j.candles) || j.candles.length === 0) return;
        const c = j.candles[0];
        const open = parseFloat(c[1]);
        const close = parseFloat(c[4]);
        if (!Number.isNaN(open) && !Number.isNaN(close)) setDailyDir(close >= open ? 'bull' : 'bear');
      } catch (e) {}
    })();
    return () => { mounted = false; };
  }, [symbol]);

  try {
    if (dailyDir) {
      priceClass += (dailyDir === 'bull') ? ' up' : ' down';
    } else if (activeSymbol === symbol && !lastCandleClosed) {
      const live = (typeof lastTick === 'number') ? lastTick : (typeof lastTick === 'string' ? parseFloat(lastTick) : NaN);
      const closed = (typeof lastPrice === 'number') ? lastPrice : (typeof lastPrice === 'string' ? parseFloat(lastPrice) : NaN);
      if (!Number.isNaN(live) && !Number.isNaN(closed)) {
        priceClass += (live >= closed) ? ' up' : ' down';
      }
    }
  } catch (e) {}

  return (
    <div className="metrics">
      <div className="metric">
        <div className="metric-label">
          <span>Last price</span>
          {activeSymbol === symbol ? (
            lastCandleClosed ? (
              <span className="badge closed">CLOSED</span>
            ) : (
              <span className="badge open">LIVE</span>
            )
          ) : null}
        </div>
        <div className={priceClass}>{displayPrice}</div>
      </div>
      <div className="metric"><div className="metric-label">Cross (confirmed)</div><div>{activeSymbol === symbol ? (confirmedCross ?? '—') : '—'}</div></div>
      <div className="metric"><div className="metric-label">{`EMA${monitorEma1}`}</div><div>{activeSymbol === symbol && ema9 ? formatNumberForDisplay(ema9) : '—'}</div></div>
      <div className="metric"><div className="metric-label">{`EMA${monitorEma2}`}</div><div>{activeSymbol === symbol && ema26 ? formatNumberForDisplay(ema26) : '—'}</div></div>
    </div>
  );
}
