import React from 'react';
import { calculateInitialEMA, updateEMA } from '../utils/ema';

// Simple chunk helper for controlled concurrency
function chunk(arr, size) {
  const out = [];
  for (let i = 0; i < arr.length; i += size) out.push(arr.slice(i, i + size));
  return out;
}

async function fetchKlines5m(symbol, limit = 300) {
  const url = `https://fapi.binance.com/fapi/v1/klines?symbol=${symbol}&interval=5m&limit=${limit}`;
  const res = await fetch(url);
  if (!res.ok) throw new Error(`klines fetch failed ${res.status}`);
  const data = await res.json();
  return data;
}

function computeEmaSnapshot(closes) {
  if (!closes || closes.length < 200) return null;
  const closesNum = closes.map((c) => parseFloat(c));
  // EMA at penultimate close
  const ema26Prev = calculateInitialEMA(closesNum.slice(0, closesNum.length - 1), 26);
  const ema200Prev = calculateInitialEMA(closesNum.slice(0, closesNum.length - 1), 200);
  // EMA at last close
  const last = closesNum[closesNum.length - 1];
  const ema26Last = updateEMA(ema26Prev, last, 26);
  const ema200Last = updateEMA(ema200Prev, last, 200);
  const statePrev = ema26Prev > ema200Prev ? 'bull' : 'bear';
  const stateNow = ema26Last > ema200Last ? 'bull' : 'bear';
  const crossedNow = statePrev !== stateNow;
  return { ema26Prev, ema200Prev, ema26Last, ema200Last, statePrev, stateNow, crossedNow, last };
}

export default function ScannerPage({ availableSymbols = [], defaultLimit = 50, serverSymbol, sseConnected, telegramConfigured }) {
  const [maxSymbols, setMaxSymbols] = React.useState(defaultLimit);
  const [onlyCross, setOnlyCross] = React.useState(false);
  const [stateFilter, setStateFilter] = React.useState('any'); // any | bull | bear
  const [results, setResults] = React.useState([]);
  const [scanning, setScanning] = React.useState(false);
  const [error, setError] = React.useState(null);

  const symbolsUsdt = React.useMemo(() => {
    const syms = (availableSymbols || []).filter((s) => /USDT$/i.test(s));
    // naive filter to avoid leveraged tokens
    return syms.filter((s) => !/(DOWN|UP|BULL|BEAR)/i.test(s));
  }, [availableSymbols]);

  const scanNow = React.useCallback(async () => {
    setScanning(true);
    setError(null);
    try {
      const list = symbolsUsdt.slice(0, Number(maxSymbols) || 0);
      const chunks = chunk(list, 8);
      const out = [];

      for (const group of chunks) {
        const groupResults = await Promise.all(
          group.map(async (symbol) => {
            try {
              const data = await fetchKlines5m(symbol, 300);
              const closes = data.map((row) => row[4]);
              const lastCloseTime = data[data.length - 1]?.[6];
              const snap = computeEmaSnapshot(closes);
              if (!snap) return null;

              const row = {
                symbol,
                lastPrice: parseFloat(closes[closes.length - 1]).toFixed(6),
                ema26: snap.ema26Last,
                ema200: snap.ema200Last,
                state: snap.stateNow,
                crossedNow: snap.crossedNow,
                time: lastCloseTime ? new Date(lastCloseTime).toLocaleString() : '',
              };
              return row;
            } catch (e) {
              // swallow errors per symbol
              return null;
            }
          })
        );
        out.push(...groupResults.filter(Boolean));
        // yield to UI a bit
        await new Promise((r) => setTimeout(r, 50));
      }

      // apply filters
      let filtered = out;
      if (onlyCross) filtered = filtered.filter((r) => r.crossedNow);
      if (stateFilter !== 'any') filtered = filtered.filter((r) => r.state === stateFilter);

      // sort: crossed first, then by symbol
      filtered.sort((a, b) => {
        if (a.crossedNow && !b.crossedNow) return -1;
        if (!a.crossedNow && b.crossedNow) return 1;
        return a.symbol.localeCompare(b.symbol);
      });

      setResults(filtered);
    } catch (e) {
      setError(e.message || String(e));
    } finally {
      setScanning(false);
    }
  }, [symbolsUsdt, maxSymbols, onlyCross, stateFilter]);

  return (
    <div>
      {/* Scanner header with title and status badges at top */}
      <div style={{ marginBottom: 16 }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
          <div style={{ fontSize: '1.2rem', fontWeight: 700 }}>Scanner</div>
          <div style={{ fontSize: '0.85rem', color: '#6b7280' }}>5m · EMA26 / EMA200</div>
        </div>
        <div style={{ fontSize: '0.8rem', display: 'flex', gap: 12, alignItems: 'center', paddingTop: 8, borderTop: '1px solid #e5e7eb' }}>
          <span>Server: {serverSymbol || 'n/a'}</span>
          <span style={{ color: sseConnected ? '#0a0' : '#a00' }}>
            {sseConnected ? 'SSE: connected' : 'SSE: disconnected'}
          </span>
          <span style={{
            padding: '2px 6px',
            borderRadius: 4,
            background: telegramConfigured == null ? '#888' : (telegramConfigured ? 'rgba(0,128,0,0.15)' : 'rgba(200,30,30,0.15)'),
            color: telegramConfigured == null ? '#fff' : (telegramConfigured ? '#0a0' : '#a00'),
            border: '1px solid ' + (telegramConfigured == null ? '#888' : (telegramConfigured ? '#0a0' : '#a00')),
            fontSize: '0.75rem'
          }}>
            Telegram: {telegramConfigured == null ? 'unknown' : (telegramConfigured ? 'configured' : 'not configured')}
          </span>
        </div>
      </div>

      {/* Controls bar — reuse app control styles for consistency */}
      <div className="controls" style={{ flexWrap: 'wrap', rowGap: 8 }}>
        <button onClick={scanNow} disabled={scanning} className="small-btn">
          {scanning ? 'Scanning…' : 'Scan Now'}
        </button>
        <div className="controls-label">
          <span className="label-text">Max symbols</span>
          <input
            type="number"
            min={1}
            max={500}
            value={maxSymbols}
            onChange={(e) => setMaxSymbols(e.target.value)}
            style={{ width: 90 }}
          />
        </div>
        <label className="control-inline-label">
          <input type="checkbox" checked={onlyCross} onChange={(e) => setOnlyCross(e.target.checked)} />
          <span className="label-text">Only new crosses (last closed)</span>
        </label>
        <div className="control-inline-label">
          <span className="label-text">State</span>
          <select value={stateFilter} onChange={(e) => setStateFilter(e.target.value)}>
            <option value="any">Any</option>
            <option value="bull">Bullish (EMA26 &gt; EMA200)</option>
            <option value="bear">Bearish (EMA26 &lt; EMA200)</option>
          </select>
        </div>
        <div style={{ marginLeft: 'auto', fontSize: 12, color: '#6b7280' }}>
          Universe: {symbolsUsdt.length} USDT pairs
        </div>
      </div>

      {error && (
        <div className="invalid-msg" style={{ marginTop: 8 }}>
          Error: {error}
        </div>
      )}

      {/* Results list — style similar to alerts list for visual consistency */}
      <div className="alerts" style={{ marginTop: 12 }}>
        <div className="alerts-title" style={{ fontSize: 14, fontWeight: 600 }}>Scan Results</div>
        <ul className="alerts-list">
          {results.length === 0 && (
            <li className="alert-item" style={{ color: '#6b7280' }}>
              {scanning ? 'Scanning…' : 'No results yet. Click "Scan Now".'}
            </li>
          )}
          {results.map((r) => (
            <li key={r.symbol} className="alert-item">
              <div className="alert-left">
                <span className={`alert-indicator ${r.state === 'bull' ? 'bull' : 'bear'}`} />
                <div className="alert-symbol">{r.symbol}</div>
              </div>
              <div className="alert-body">
                <div className="alert-info">
                  <div className="alert-type-short">{r.state}</div>
                  <div className="alert-time">{r.time}</div>
                </div>
                <div className="alert-right">
                  <div className="alert-price">{r.lastPrice}</div>
                  <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>
                    EMA26 {r.ema26?.toFixed(6)} · EMA200 {r.ema200?.toFixed(6)}
                    {r.crossedNow ? ' · crossed' : ''}
                  </div>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
