import React from 'react';
import { NavLink } from 'react-router-dom';

export default function Header({ title, subtitle, serverSymbol, sseConnected, telegramConfigured, toast }) {
  return (
    <div className="header">
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <div>
          <div className="title" style={{ fontWeight: 800 }}>{title}</div>
          {subtitle && <div style={{ fontSize: '0.85rem', color: '#6b7280' }}>{subtitle}</div>}
        </div>

        <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
          <div style={{ display: 'flex', gap: 8, marginRight: 8 }}>
            <NavLink to="/" end style={({ isActive }) => ({
              padding: '6px 10px', borderRadius: 6, border: '1px solid ' + (isActive ? '#0a0' : '#ccc'),
              background: isActive ? 'rgba(0,128,0,0.08)' : 'transparent', color: isActive ? '#0a0' : '#333', textDecoration: 'none', fontWeight: 600
            })}>Alert</NavLink>
            <NavLink to="/scanner" style={({ isActive }) => ({
              padding: '6px 10px', borderRadius: 6, border: '1px solid ' + (isActive ? '#0a0' : '#ccc'),
              background: isActive ? 'rgba(0,128,0,0.08)' : 'transparent', color: isActive ? '#0a0' : '#333', textDecoration: 'none', fontWeight: 600
            })}>Scanner</NavLink>
          </div>
          <div style={{ fontSize: '0.85rem', color: '#6b7280' }}>{subtitle}</div>
          <div style={{fontSize: '0.8rem', display: 'flex', gap: 12, alignItems: 'center'}}>
            <span>Server: {serverSymbol || 'n/a'}</span>
            <span style={{color: sseConnected ? '#0a0' : '#a00'}}>{sseConnected ? 'SSE: connected' : 'SSE: disconnected'}</span>
            <span style={{
              padding: '2px 6px', borderRadius: 4,
              background: telegramConfigured == null ? '#888' : (telegramConfigured ? 'rgba(0,128,0,0.15)' : 'rgba(200,30,30,0.15)'),
              color: telegramConfigured == null ? '#fff' : (telegramConfigured ? '#0a0' : '#a00'),
              border: '1px solid ' + (telegramConfigured == null ? '#888' : (telegramConfigured ? '#0a0' : '#a00')),
              fontSize: '0.75rem'
            }}>
              Telegram: {telegramConfigured == null ? 'unknown' : (telegramConfigured ? 'configured' : 'not configured')}
            </span>
          </div>
        </div>
      </div>

      {/* render toast if provided */}
      {toast && (
        <div style={{position: 'absolute', top: 12, right: 12, padding: '8px 12px', background: toast.ok ? 'rgba(0,128,0,0.9)' : 'rgba(200,30,30,0.95)', color: '#fff', borderRadius: 6, zIndex: 9999, boxShadow: '0 2px 8px rgba(0,0,0,0.3)'}}>
          <div style={{fontSize: '0.9rem', fontWeight: 600}}>{toast.ok ? 'Success' : 'Error'}</div>
          <div style={{fontSize: '0.8rem', marginTop: 4}}>{toast.message}</div>
        </div>
      )}
    </div>
  );
}
