import React, { useEffect, useMemo, useState, useRef, useCallback } from 'react';
import scannerManager from '../lib/scannerManager';

export default function ScannerPage({ availableSymbols, fetchExchangeInfo, monitorMinutes, setMonitorMinutes, monitorEma1, setMonitorEma1, monitorEma2, setMonitorEma2 }) {
  const symbols = useMemo(() => (Array.isArray(availableSymbols) ? availableSymbols.slice() : []), [availableSymbols]);
  const [state, setState] = useState(scannerManager.getState());

  useEffect(() => {
    // provide symbol list provider to manager
    scannerManager.setGetSymbols(() => symbols || []);
  }, [symbols]);

  useEffect(() => {
    // ensure we have exchange info loaded
    if ((!symbols || symbols.length === 0) && typeof fetchExchangeInfo === 'function') {
      try { fetchExchangeInfo(); } catch (e) {}
    }
  }, [symbols, fetchExchangeInfo]);

  useEffect(() => {
    // subscribe to manager updates
    const off = scannerManager.onUpdate((s) => setState(s));
    // initialize state
    setState(scannerManager.getState());
    return () => off();
  }, []);

  function startGolden() { try { scannerManager.start('golden', { interval: monitorMinutes, emaShort: monitorEma1, emaLong: monitorEma2 }); } catch (e) {} }
  function startDead() { try { scannerManager.start('dead', { interval: monitorMinutes, emaShort: monitorEma1, emaLong: monitorEma2 }); } catch (e) {} }
  function stopScan() { try { scannerManager.stop(); } catch (e) {} }

  const running = !!state.running;
  const currentSymbol = state.currentSymbol;
  const progress = state.progress || { done: 0, total: 0 };
  const results = useMemo(() => (Array.isArray(state.results) ? state.results.slice() : []), [state.results]);

  // modal state for newly detected matches
  const [modalVisible, setModalVisible] = useState(false);
  const [modalData, setModalData] = useState(null);
  const shownResultsRef = useRef(new Set());
  // server scan controls
  const [serverConcurrency, setServerConcurrency] = useState(3);
  const [serverBatchDelay, setServerBatchDelay] = useState(300);
  const [serverScanRunning, setServerScanRunning] = useState(false);
  const [serverScanProgress, setServerScanProgress] = useState({ done: 0, total: 0 });

  // small beep helper (reuse pattern from App.beep)
  function beep() {
    try {
      const AudioCtx = window.AudioContext || window.webkitAudioContext;
      const ctx = new AudioCtx();
      const o = ctx.createOscillator();
      const g = ctx.createGain();
      o.type = 'sine';
      o.frequency.value = 880;
      o.connect(g);
      g.connect(ctx.destination);
      o.start();
      g.gain.setValueAtTime(0.0001, ctx.currentTime);
      g.gain.exponentialRampToValueAtTime(0.1, ctx.currentTime + 0.01);
      g.gain.exponentialRampToValueAtTime(0.0001, ctx.currentTime + 0.6);
      setTimeout(() => {
        o.stop();
        try { ctx.close(); } catch (e) {}
      }, 700);
    } catch (e) {
      // ignore
    }
  }

  // show modal + beep when a new match is detected
  useEffect(() => {
    try {
      if (!results || results.length === 0) return;
      const top = results[0];
      if (!top || !top.symbol) return;
      const id = `${top.symbol}::${top.time}::${top.emaShort}::${top.emaLong}`;
      if (shownResultsRef.current.has(id)) return;
      // mark shown and present modal
      shownResultsRef.current.add(id);
      try { beep(); } catch (e) {}
      setModalData(top);
      setModalVisible(true);
    } catch (e) {}
  }, [results]);

  // Server-side scan controls: start/stop/status
  const serverUrl = process.env.REACT_APP_SERVER_URL || 'http://localhost:3001';
  const fetchServerScanStatus = useCallback(async () => {
    try {
      const res = await fetch(`${serverUrl}/scan/status`);
      if (!res.ok) return null;
      const j = await res.json();
      return j;
    } catch (e) { return null; }
  }, [serverUrl]);

  async function startServerScan() {
    try {
      const body = { type: 'golden', interval: `${monitorMinutes}m`, emaShort: monitorEma1, emaLong: monitorEma2, concurrency: Number(serverConcurrency), batchDelay: Number(serverBatchDelay) };
      await fetch(`${serverUrl}/scan/start`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(body) });
      setServerScanRunning(true);
      // fetch initial status
      const j = await fetchServerScanStatus();
      if (j && j.progress) setServerScanProgress(j.progress);
    } catch (e) {
      console.warn('startServerScan error', e);
    }
  }

  async function stopServerScan() {
    try {
      await fetch(`${serverUrl}/scan/stop`, { method: 'POST' });
      setServerScanRunning(false);
    } catch (e) {
      console.warn('stopServerScan error', e);
    }
  }

  // (polling handled below)

  // poll server scan status while running
  useEffect(() => {
    let id = null;
    if (serverScanRunning) {
      id = setInterval(() => { fetchServerScanStatus(); }, 2000);
      // initial
      fetchServerScanStatus();
    }
    return () => { if (id) clearInterval(id); };
  }, [serverScanRunning]);

  return (
    <div className="alerts" style={{ padding: 8 }}>
      {modalVisible && modalData && (
        <div className="modal-overlay" role="dialog" aria-modal="true">
          <div className="modal-card">
            <div className="modal-title">Match detected: {String(modalData.symbol || '')}</div>
            <div className="modal-sub">Interval: {String(modalData.interval || '')} Â· EMA {String(modalData.emaShort || '')} / EMA {String(modalData.emaLong || '')}</div>
            <div className="modal-time">{String(modalData.time || '')}</div>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
              <button className="secondary" onClick={() => { try { setModalVisible(false); } catch (e) {} }}>Close</button>
            </div>
          </div>
        </div>
      )}
      <div className="alerts-title">Binance EMA Cross Scanner</div>
  <div className="panel scanner-panel">
        <div className="scanner-controls-left">
          <label className="control-inline-label">
            <span className="label-text">Mins</span>
            <input type="number" min="1" value={monitorMinutes} onChange={(e) => setMonitorMinutes(Number(e.target.value || 0))} />
          </label>
          <label className="control-inline-label">
            <span className="label-text">EMA1</span>
            <input type="number" min="1" value={monitorEma1} onChange={(e) => setMonitorEma1(Number(e.target.value || 0))} />
          </label>
          <label className="control-inline-label">
            <span className="label-text">EMA2</span>
            <input type="number" min="1" value={monitorEma2} onChange={(e) => setMonitorEma2(Number(e.target.value || 0))} />
          </label>
        </div>

        <div className="scanner-controls">
          <button onClick={startGolden} disabled={running} className="scanner-btn green">Golden</button>
          <button onClick={startDead} disabled={running} className="scanner-btn red">Dead</button>
          <button onClick={stopScan} disabled={!running} className="scanner-btn stop">Stop</button>
        </div>
      </div>

      {/* Server-side scan controls */}
      <div className="panel" style={{ marginTop: 8, flexWrap: 'wrap', gap: 8 }}>
        <label className="control-inline-label">
          <span className="label-text">Server concurrency</span>
          <input type="number" min="1" value={serverConcurrency} onChange={(e) => setServerConcurrency(Number(e.target.value || 1))} />
        </label>
        <label className="control-inline-label">
          <span className="label-text">Batch delay (ms)</span>
          <input type="number" min="0" value={serverBatchDelay} onChange={(e) => setServerBatchDelay(Number(e.target.value || 0))} />
        </label>
        <div style={{ marginLeft: 'auto', display: 'flex', gap: 8, alignItems: 'center' }}>
          <button className="scanner-btn green" onClick={() => startServerScan()} disabled={serverScanRunning}>Start Server Scan</button>
          <button className="scanner-btn stop" onClick={() => stopServerScan()} disabled={!serverScanRunning}>Stop Server Scan</button>
        </div>
        <div style={{ width: '100%', marginTop: 6, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div className="scanner-progress-left">Server scan: {serverScanRunning ? 'running' : 'idle'}</div>
          <div className="scanner-progress-right">{serverScanProgress.done}/{serverScanProgress.total}</div>
        </div>
      </div>

      {/* progress bar: shows scanning progress below the buttons */}
      <div className="progress-wrap" aria-hidden={!running}>
        <div className="scanner-progress-container">
          <div className="scanner-progress-left">{running ? `Scanning: ${currentSymbol || '...'}` : 'Idle'}</div>
          <div className="scanner-progress-right">{progress.done}/{progress.total || (symbols || []).filter(s => /USDT$/i.test(s)).length}</div>
        </div>
        <div className="progress-track">
          {(() => {
            const total = (progress && progress.total) ? progress.total : ((symbols || []).filter(s => /USDT$/i.test(s)).length || 0);
            const done = (progress && progress.done) ? progress.done : 0;
            const pct = total > 0 ? Math.min(100, Math.round((done / total) * 100)) : 0;
            return (
              <div role="progressbar" aria-valuemin={0} aria-valuemax={100} aria-valuenow={pct} className="progress-fill" style={{ width: `${pct}%` }} />
            );
          })()}
        </div>
      </div>

      <ul className="alerts-list">
        {results.length === 0 ? (
          <li className="alert-item no-results">No matches yet.</li>
        ) : (
          results.map((r, idx) => (
            <li key={`${r.symbol}-${idx}`} className="alert-item">
              <div className="alert-left">
                <span className={`alert-indicator bull`} />
                <div className="alert-symbol">{r.symbol}</div>
              </div>
              <div className="alert-body">
                <div className="alert-info">
                  <div className="alert-type-short">Match</div>
                  <div className="alert-time">{r.time}</div>
                </div>
                <div className="alert-right">
                  <div className="alert-price">{Number(r.last26).toFixed(6)} / {Number(r.last200).toFixed(6)}</div>
                </div>
              </div>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}
